INSERT INTO `kg_news` (`id`,`typeId`,`sourceId`,`attributeId`,`newsTitle`,`summary`,`thumbnail`,`author`,`content`,`sequence`,`indexshow`,`createDate`,`viewsCount`,`OBJECT_VERSION_NUMBER`,`REQUEST_ID`,`PROGRAM_ID`,`CREATED_BY`,`CREATION_DATE`,`LAST_UPDATED_BY`,`LAST_UPDATE_DATE`,`LAST_UPDATE_LOGIN`,`CERTIFICATE_TYPE`,`EFFECTIVE_START_DATE`,`EFFECTIVE_END_DATE`,`ATTRIBUTE_CATEGORY`) VALUES 
 (1062,17,2,'','《上海市人民政府关于促进民办教育健康发展的实施意见》','上海市人民政府关于促进民办教育健康发展的实施意见','',NULL,'<p style=\"text-align: center; line-height: 29px; white-space: normal;\"><strong><span style=\"font-family: 微软雅黑, sans-serif; font-size: 21px;\">上海市人民政府关于促进民办教育健康发展的实施意见</span></strong></p><p style=\"text-align: center; line-height: 29px; white-space: normal;\"><strong><span style=\"font-family: 宋体;\">沪府发〔</span></strong><strong>2017</strong><strong><span style=\"font-family: 宋体;\">〕</span></strong><strong>94</strong><strong><span style=\"font-family: 宋体;\">号</span></strong></p><p style=\"text-align: center; line-height: 29px; white-space: normal;\"><span style=\"font-family: Calibri;\">&nbsp;</span><br/></p><p style=\"line-height: 29px; white-space: normal;\"><strong><span style=\"font-family: 宋体;\">各区人民政府，市政府各委、办、局：</span></strong></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　为全面贯彻《中华人民共和国民办教育促进法》和《国务院关于鼓励社会力量兴办教育促进民办教育健康发展的若干意见》（国发〔</span><span style=\"font-family: Calibri;\">2016</span><span style=\"font-family: 宋体;\">〕</span><span style=\"font-family: Calibri;\">81</span><span style=\"font-family: 宋体;\">号，以下简称《若干意见》），结合本市实际，现提出促进上海民办教育健康发展的实施意见如下：</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>一、明确总体要求</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><strong><span style=\"font-family: 宋体;\">　　（一）重要意义</span></strong></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　改革开放以来，上海民办教育以提高教育质量为核心任务，注重机制创新，突出内涵建设，各级各类民办学校全面、协调、健康、有序发展。民办教育的发展，适应了经济社会发展需要，丰富了教育资源供给、深化了办学体制机制改革、创新了人才培养模式、满足了人民群众日益增长的多样化教育需求。民办教育已经成为上海教育事业的重要组成部分，是促进教育改革的重要力量和教育发展的重要增长点，为上海率先基本实现教育现代化做出了积极贡献。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（二）指导思想和基本原则</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　全面贯彻落实党的十九大精神，以习近平新时代中国特色社会主义思想为指导，牢固树立并切实贯彻创新、协调、绿色、开放、共享五大发展理念，全面贯彻党的教育方针，坚持社会主义办学方向，坚持立德树人，培育和践行社会主义核心价值观。从国家发展战略高度出发，服务上海“四个中心”和社会主义现代化国际大都市建设，全面落实《若干意见》中提出的各项政策措施，鼓励和引导社会力量兴办教育，促进民办教育持续健康发展，培养德智体美全面发展的社会主义建设者和接班人。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　全面贯彻实施《若干意见》，应坚持“育人为本、德育为先，分类管理、公益导向，优化环境、综合施策，依法管理、规范办学，鼓励改革、上下联动”的基本原则。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（三）实施目标</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　全面深化教育综合改革，大力扶持和引导社会力量兴办教育，提升民办教育治理水平、创新政府扶持机制、提高学校办学质量、彰显办学特色、规范办学行为，促进各级各类民办教育健康发展，初步建立适应上海城市发展定位要求，满足人民群众多样化、多层次教育需求的民办教育体系。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>二、加强党对民办学校的领导</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><strong><span style=\"font-family: 宋体;\">　　（一）切实加强民办学校党的建设</span></strong></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　贯彻落实《中共中央办公厅印发〈关于加强民办学校党的建设工作的意见（试行）〉的通知》（中办发〔</span><span style=\"font-family: Calibri;\">2016</span><span style=\"font-family: 宋体;\">〕</span><span style=\"font-family: Calibri;\">78</span><span style=\"font-family: 宋体;\">号），全面加强民办学校党的政治建设、思想建设、组织建设、作风建设、纪律建设，增强政治意识、大局意识、核心意识、看齐意识。坚持党的组织和党的工作全面覆盖原则，落实党的工作与民办学校发展，同步谋划、同步设置、同步开展。实现学校基层党组织全覆盖、党建工作上水平，有效发挥基层党组织的战斗堡垒作用和共产党员的先锋模范作用。积极做好党员发展和教育管理服务工作。制定关于进一步加强上海市民办高校党的建设工作的意见。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　学校党组织领导班子成员的选配与任免，要报上级党组织批准。积极落实“双向进入、交叉任职”的规定，选好配强民办学校党组织负责人。民办学校党组织书记应当通过法定程序进入学校董（理）事会，办学规模大、党员人数多的学校，符合条件的专职副书记也可进入董（理）事会。党组织班子成员应按照学校章程进入行政管理层，党员校长、副校长等行政领导班子成员，可按照党内有关规定进入党组织班子。完善民办高校党政干部选聘机制，制定《上海市民办高校党组织领导干部选拔任用暂行办法》。民办高校党组织负责人兼任政府派驻学校的督导专员。涉及民办学校发展规划、重要改革、人事安排等重大事项，党组织要参与讨论研究，董（理）事会在作出决定前，要征得党组织同意；涉及党的建设、思想政治工作和德育工作的事项，要由党组织研究决定。坚持党建带群建，加强民办学校工会、共青团、妇联、教代会、学代会等群众组织和学生社团建设。把民办学校党组织建设、党对民办学校的领导作为民办学校年度检查的重要内容。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（二）加强和改进民办学校思想政治教育工作</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　全面贯彻中央和本市关于加强和改进新形势下学校思想政治工作的部署，把思想政治教育工作纳入学校事业发展规划，把思想政治工作队伍建设纳入学校人才队伍培养规划，全面提升思想政治教育工作水平。制订实施民办高校党建和思想政治工作创新专项计划。切实加强思想政治理论课和思想品德类课程、教材、教师队伍建设，深入开展学科德育、课程思政教育教学改革，深入推进中国特色社会主义理论体系和党的十九大精神进教材、进课堂、进头脑，把社会主义核心价值观融入教育教学全过程、教书育人各环节，不断增强广大师生中国特色社会主义道路自信、理论自信、制度自信、文化自信。加强网络思政和易班建设，充分发挥网络思想政治教育功能。提高思想政治教育的针对性、实效性和吸引力、感染力，切实加强理想信念、爱国主义、集体主义、中国特色社会主义教育和中华优秀传统文化、革命传统文化、社会主义先进文化、民族团结教育，引导学生树立正确的世界观、人生观、价值观。大力开展社会实践和志愿服务（公益劳动），积极开展心理健康教育，加强心理健康教育教师配备。大力弘扬主旋律、传播正能量，全面提高教书育人、实践育人、科研育人、管理育人、服务育人、文化育人、组织育人的水平。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>三、推进民办学校分类管理改革</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><strong><span style=\"font-family: 宋体;\">　　（一）落实分类管理制度</span></strong></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　制定《上海市民办学校分类许可登记管理办法》。对民办学校（含其他民办教育机构）实行非营利性和营利性分类管理，确保已设立的民办学校实现平稳过渡。举办者自主选择举办非营利性民办学校或者营利性民办学校，依法依规办理许可和登记。其中，实施义务教育的学校，不得登记为营利性民办学校。非营利性民办学校举办者不取得办学收益，办学结余全部用于办学，终止时的剩余财产继续用于其他非营利性学校办学。营利性民办学校举办者可以取得办学收益，办学结余依据国家有关规定进行分配。坚持教育的公益属性，积极鼓励和大力支持社会力量举办非营利性民办学校，无论是非营利性民办学校还是营利性民办学校都要始终把社会效益放在首位。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（二）明确已设学校过渡安排</strong></span></p><p style=\"line-height: 29px; white-space: normal;\">　　<span style=\"font-family: Calibri;\">2016</span><span style=\"font-family: 宋体;\">年</span><span style=\"font-family: Calibri;\">11</span><span style=\"font-family: 宋体;\">月</span><span style=\"font-family: Calibri;\">7</span><span style=\"font-family: 宋体;\">日前设立的登记为民办非企业单位法人的现有学校（以下简称“现有学校”），其举办者可以自主选择举办非营利性民办学校或者营利性民办学校，政府按照分类管理原则，根据其完成非营利性或者营利性选择以及后续相关工作的开展情况，予以差别化的扶持和管理，实现分类管理。对过渡阶段的现有学校，根据其法人属性予以管理。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　经营性民办培训机构应当依法修订章程、健全法人治理结构、完善办学条件，在</span><span style=\"font-family: Calibri;\">2019</span><span style=\"font-family: 宋体;\">年</span><span style=\"font-family: Calibri;\">12</span><span style=\"font-family: 宋体;\">月</span><span style=\"font-family: Calibri;\">31</span><span style=\"font-family: 宋体;\">日前，取得办学许可证并办理完成其他相关手续。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（三）推进现有学校有序过渡</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　现有学校的举办者应当在</span><span style=\"font-family: Calibri;\">2018</span><span style=\"font-family: 宋体;\">年</span><span style=\"font-family: Calibri;\">12</span><span style=\"font-family: 宋体;\">月</span><span style=\"font-family: Calibri;\">31</span><span style=\"font-family: 宋体;\">日前，向主管部门提交关于学校办学属性选择的书面材料，未按期提交材料的学校不得转设为营利性民办学校。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　选择登记为非营利性民办学校的，应当在</span><span style=\"font-family: Calibri;\">2019</span><span style=\"font-family: 宋体;\">年</span><span style=\"font-family: Calibri;\">12</span><span style=\"font-family: 宋体;\">月</span><span style=\"font-family: Calibri;\">31</span><span style=\"font-family: 宋体;\">日前，依法修订学校章程、完善法人治理结构和内部管理制度、继续办学。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　选择登记为营利性民办学校的，应当由学校组织进行财务清算，依法明确资产权属，按照国家和本市规定缴纳相关税费，重新办理法人登记手续，继续办学。其中，主要实施高等学历教育的学校，应当在</span><span style=\"font-family: Calibri;\">2021</span><span style=\"font-family: 宋体;\">年</span><span style=\"font-family: Calibri;\">12</span><span style=\"font-family: 宋体;\">月</span><span style=\"font-family: Calibri;\">31</span><span style=\"font-family: 宋体;\">日前完成上述工作；其他学校应当在</span><span style=\"font-family: Calibri;\">2020</span><span style=\"font-family: 宋体;\">年</span><span style=\"font-family: Calibri;\">12</span><span style=\"font-family: 宋体;\">月</span><span style=\"font-family: Calibri;\">31</span><span style=\"font-family: 宋体;\">日前完成上述工作。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（四）完善退出机制</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　各有关部门、各单位应结合实际，健全各类民办学校退出机制，依法保护受教育者、教职工和举办者的合法权益。民办学校终止时，财产处置按照有关法律规定和学校章程处理。其中，新设立的非营利性民办学校终止时，清偿后的剩余财产继续用于其他非营利性民办学校办学。现有学校选择登记为非营利性民办学校后终止，或者未及选择直接终止，民办学校的财产依法清偿后有剩余的，按照有关规定给予出资者相应的补偿或者奖励，其余财产继续用于其他非营利性学校办学。营利性民办学校终止时，应当进行财务清算，清偿后的剩余财产依照公司法和学校章程中的有关规定处理。民办学校终止，应当及时办理注销办学许可和法人登记等手续。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（五）建立补偿奖励机制</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　现有学校选择登记为非营利性民办学校后终止，或者未及选择直接终止，妥善安置受教育者和教职工并且规范开展相关工作的，根据出资者的申请，由主管部门会同相关职能部门综合考虑其在</span><span style=\"font-family: Calibri;\">2017</span><span style=\"font-family: 宋体;\">年</span><span style=\"font-family: Calibri;\">9</span><span style=\"font-family: 宋体;\">月</span><span style=\"font-family: Calibri;\">1</span><span style=\"font-family: 宋体;\">日前的出资、取得合理回报的情况以及办学效益等因素，从学校依法清偿后的剩余财产中给予出资者相应的补偿或奖励。补偿与奖励从学校剩余财产中的货币资金提取；货币资金不足的，从将其他资产依法转让后获得的货币资金中提取。剩余财产在扣除对出资者的补偿或奖励后，其余继续用于其他非营利性学校办学。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>四、鼓励社会力量办学</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><strong><span style=\"font-family: 宋体;\">　　（一）完善准入制度</span></strong></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　支持社会力量进入各级各类教育，提供优质教育资源，进一步优化民办教育生态。社会力量投入教育，只要是不属于法律法规禁止进入以及不损害第三方利益、社会公共利益、国家安全的领域，政府不得限制。实行准入负面清单制度，简政放权，推进一站式受理、窗口服务，吸引更多社会资源投入教育。支持社会力量举办各种层次类型的非营利性民办学校，鼓励社会力量举办或参与举办职业教育、继续教育、老年教育、社区教育和特殊教育等。民办学校的设置应当参照国家同级同类学校设置标准。制定完善上海市民办中小学、幼儿园、培训机构设置标准，规范审批程序、审批材料目录及相应的格式化文本。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（二）创新教育投融资机制</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　多渠道吸引社会资金，扩大办学资金来源。鼓励社会资金进入教育领域举办学校或者投入项目建设。鼓励金融机构在风险可控前提下开发适合民办学校特点的金融产品，积极运用信贷、租赁、保险等多种金融手段支持民办学校发展，探索办理民办学校未来收入、应收账款、知识产权质押贷款业务。探索营利性民办学校以有偿取得的土地、设施等财产进行抵押融资，或根据自身发展需要而进行股权质押等投融资改革。引导社会力量创新教育投融资模式，依法依规为民办学校提供特色化、专业化的贷款、担保等服务。鼓励社会力量对非营利性民办学校给予捐赠。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（三）试点多元主体合作办学</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　探索创新政府与社会资本在民办教育领域的合作机制，鼓励社会资本参与教育基础设施建设和运营管理、提供专业化服务。支持和吸引社会组织和公民个人以独资、合资、合作等多种方式参与办学，举办者可以用资金、实物、土地使用权、知识产权以及其他财产作为办学出资。探索举办混合所有制职业院校，允许管理者和骨干教师以资本、知识、技术、管理等要素参与办学并享有相应权利，在学校管理、人员聘用、人才培养、财务管理等方面充分发挥多元主体办学的体制优势。鼓励营利性民办学校建立股权激励机制。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>五、完善政府扶持政策</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（一）落实税费优惠政策</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　民办学校按照国家有关规定享受相关税收优惠政策。对民办学校自用的房产、土地，免征房产税、城镇土地使用税。企业、个人通过公益性社会团体或者县级以上政府及其部门支持教育事业的公益性捐赠支出，按规定享受税收优惠政策。符合条件的非营利性民办学校与公办学校享有同等待遇，获得非营利组织免税资格的民办学校，符合免税条件的收入免征企业所得税。捐资建设校舍及开展表彰资助等活动的冠名，依法尊重捐赠人意愿。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　民办学校用电、用水、用气等，执行与公办学校相同的价格政策。民办学校的举办者以不动产作为出资，因履行出资义务需要将有关不动产登记到民办学校名下的，只缴纳证照工本费和登记费。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（二）实行差别化用地政策</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　民办学校建设用地按照科教用地管理。非营利性民办学校享受公办学校同等政策，可以按照划拨等方式供应土地。营利性民办学校按照国家相应的政策供给土地，只有一个意向用地者的，可按照协议方式供地。土地使用权人申请改变全部或者部分土地用途的，政府应当将申请改变用途的土地收回，按时价定价，重新依法供应。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（三）试点市场化收费改革</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　制订民办教育收费管理办法，实行分类管理政策，规范民办学校收费，逐步扩大民办学校收费自主权。新设立或者完成过渡手续的非营利性民办学校收费，通过市场化改革试点，逐步稳妥推行市场调节价。营利性民办学校收费实行市场调节价，具体收费标准由民办学校自主确定。完善民办学校学费专户管理和收费公示等制度，健全民办学校收费监管机制。在过渡期间，民办学校需要调整现行教育收费标准的，应先按规定完成办学属性的重新登记，再按照程序调整收费标准。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（四）加大财政投入力度</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　根据国家要求，因地制宜，调整优化教育支出结构，逐步加大对民办教育的财政扶持力度。各区可设立促进民办教育发展专项资金，将支持民办教育发展资金列入年度同级教育财政预算，向社会公开，接受审计和社会监督，提高资金使用效益。健全完善市、区两级专项资金的使用和管理办法，优化专项资金支出结构，鼓励、扶持、促进民办学校内涵发展和特色创建，支持和鼓励民办学校开展职业能力培训，加大财政对民办学校职业培训基础能力建设的支持力度，构建促进民办教育发展的公共服务平台，推动民办教育重大改革和发展。探索试点政府资金支持符合条件的非营利性学校的教育教学等设施建设。市、区两级政府明确政府补贴的项目、对象、标准、用途。健全义务教育阶段民办学校经费扶持机制，对义务教育阶段民办学校，按照不低于生均公用经费基准定额的标准给予补助。健全以招收进城务工人员随迁子女为主的民办小学办学成本政府补贴制度。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（五）完善购买服务制度</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　鼓励向民办学校购买就读学位、课程教材、科研成果、职业培训、政策咨询等教育服务，不断完善购买项目、标准和程序，制定政府购买教育服务制度，完善购买服务绩效评价机制。支持民办学校与公办学校在管理、课程、科研等方面探索资源共享，积极鼓励公办学校与民办学校相互购买管理服务、教学资源、科研成果，形成相互委托管理和相互购买服务的新机制。探索不改变薄弱公办中小学公益属性的前提下由民办学校进行委托管理，鼓励民办中小学参与集团化办学。因地制宜开展地段内学生就近入读民办中小学与幼儿园的购买学位工作。鼓励民办学校开发适应市场和社会需要的各类教育公共服务项目，提高承接政府购买服务的能力。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（六）健全完善基金制度</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　继续做大做强上海市民办教育发展基金会，市有关部门对上海市民办教育发展基金会予以支持。鼓励社会力量按照国家关于基金会管理的规定，设立民办教育发展基金会，充分发挥基金会在筹集社会资源和资金、非营利性民办学校资金支持、民办学校终止办学的剩余资产处置、非营利性民办学校改革发展和特色创新、促进公益性强的优质民办教育机构健康成长、鼓励社会力量兴办教育等方面的作用。引导营利性民办学校合作设立投资基金，用于学校创新发展，防范办学风险。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>六、落实现代学校制度</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（一）健全学校法人治理结构</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　规范民办学校章程，健全学校决策机制。健全董（理）事会和监事（会）制度，董（理）事会和监事（会）成员依据学校章程规定的权限和程序共同参与学校的办学和管理。董（理）事会应当优化人员构成，由举办者或者其代表、校长、党组织负责人、教职工代表等共同组成。监事会中应当有党组织领导班子成员。探索实行独立董（理）事、监事制度。学校党组织要支持学校决策机构和校长依法行使职权，督促其依法治教、规范管理。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（二）规范关键岗位选聘机制</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　学校关键管理岗位实行亲属回避制度，建立适应自身发展的标准化内部管理体系。探索职业校长制和公开选聘机制，依法保障校长行使管理权。民办学校校长应当熟悉教育及相关法律法规，具有</span><span style=\"font-family: Calibri;\">5</span><span style=\"font-family: 宋体;\">年以上教育管理经验和良好办学业绩，个人信用状况良好。一个自然人不得兼任同一个学校的董（理）事和监事。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　<strong>　七、强化规范发展</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><strong><span style=\"font-family: 宋体;\">　　（一）落实学校法人财产权</span></strong></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　民办学校应当明确产权关系，建立健全资产管理制度。民办学校举办者应依法履行出资义务，将出资用于办学的土地、校舍和其他资产足额过户到学校名下。存续期间，民办学校对举办者的出资、国有资产、受赠的财产以及办学积累享有法人财产权，任何组织和个人不得侵占、挪用、抽逃。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（二）健全资产和财务管理</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　进一步规范民办学校会计核算，建立健全学校内部控制管理制度和第三方审计制度。非营利性和营利性民办学校按照登记的法人属性，根据国家有关规定执行相应的会计制度。民办学校要明晰财务管理，依法设置会计账簿。民办学校应将举办者出资、政府补助、受赠、收费、办学积累等各类资产分类登记入账，定期开展资产清查，并将清查结果向社会公布。民办学校的办学经费只能在学校的资金账户中统一核算和使用，不得转往学校账户以外的资金账户。健全完善民办学校财务监管平台，建立民办学校财务评估体系。进一步完善民办学校财务管理办法和会计核算办法，制定民办学校会计基础工作规范管理指导意见，健全年度财务预算报告和决算报告报备制度。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（三）规范学校办学行为</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　民办学校要诚实守信、规范办学。办学条件应符合国家和地方规定的设置标准和有关要求，在校生数要控制在审批机关核定的办学规模内。要按照国家和地方有关规定做好招生广告与简章的备案和发布工作，依法规范宣传与招生。各级各类民办学校应当按照国家规定颁发相应的证书或者发给证明文件。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（四）明确安全管理责任</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　民办学校应当遵守国家有关安全法律、法规和规章，重视校园安全工作，确保校园安全技术防范系统建设符合国家和地方有关标准，学校选址和校舍建筑符合国家抗震设防、消防技术、应急避难等相关标准。建立健全安全管理制度和应急机制，制定和完善突发事件应急预案，定期开展安全检查、巡查，及时发现和消除安全隐患。加强受教育者和教职工安全教育培训，定期开展针对上课、课间、午休等不同场景的安全演练，提高师生安全意识和逃生自救能力。建立安全工作组织机构，配备学校内部安全保卫人员，明确安全工作职责。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>八、提高办学质量</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（一）明确学校办学定位</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　积极引导民办学校服务社会需求，更新办学理念，深化教育教学改革，创新办学模式，加强内涵建设，提高办学质量。支持创办理念先进、课程设置多样、注重内涵发展和特色建设的民办中小学和幼儿园，继续实施民办中小学特色学校（项目）、民办优质幼儿园（项目）创建活动。职业院校应明确技术技能人才培养定位，服务区域经济和产业发展，深化产教融合、校企合作，提高技术技能型人才培养水平。鼓励举办应用技术类本科高等学校，培养适应经济结构调整、产业转型升级和新产业、新业态、新商业模式需要的人才。试点建设高水平民办高校和应用型特色高职，支持民办高校进行中高职、应用本科贯通培养改革，探索应用型人才产学合作培养模式，支持符合条件的民办高校设立专业硕士学位点，提升上海民办高校的办学层次和办学水平。充分发挥民办教育在完善终身教育体系、构建学习型社会中的积极作用。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（二）保障依法自主办学</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　扩大民办高等学校和中等职业学校专业设置自主权，鼓励学校根据国家战略需求和区域产业发展需要，依法依规设置和调整学科专业。落实国家和上海关于招生的规定，支持民办学校参与考试招生制度改革。鼓励社会声誉好、教学质量高、就业有保障的民办高等职业学校，在核定的办学规模内，进一步优化招生计划分配模式、探索创新招生选拔机制、改进录取方式。民办中小学在完成国家规定课程前提下，自主实施教育教学活动。中等以下层次民办学校根据全市统一规定，在核定的办学规模内，面向社会自主招生。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（三）培育优质教育资源</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　鼓励支持高水平有特色民办学校培育优质学科、专业、课程、师资、管理，整体提升教育教学质量，着力打造一批具有国际影响力和竞争力的民办教育品牌，着力培养一批有理想、有境界、有情怀、有担当的民办教育家。设立民办教育人才培养专项计划，培养一批民办教育专业研究人员、学校管理专业人员。鼓励民办高等学校和中等职业学校与世界高水平同类学校在学科、专业、课程建设以及人才培养等方面开展交流。引导行业企业与学校加强合作，鼓励具备资质的社会组织与企业建立体现职业教育特点的评估体系，把行业标准和岗位要求作为职业教育质量评价的重要依据。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>九、保障师生权益</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><strong><span style=\"font-family: 宋体;\">　　（一）落实教师同等待遇</span></strong></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　落实跨统筹地区社会保险关系转移接续政策，完善民办学校教师户籍迁移等方面的服务政策，探索创新民办学校教师聘任和交流制度。民办学校教师在资格认定、职务评聘、培养培训、评优表彰、教龄和工龄计算、社会活动等方面，与公办学校教师享有同等权利。非营利性民办学校教师享受当地公办学校同等的人才引进政策。引导鼓励民办学校建立教师收入与办学效益动态调整机制，合理提高人员经费在学校支出中的比例。探索建立民办学校教师从教奖励制度。吸引各类高层次人才到民办学校任教，做到事业留人、感情留人、待遇留人。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（二）保障学生同等权利</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　民办学校学生在评奖评优、升学就业、社会优待、医疗保险、助学贷款、奖助学金等方面与同级同类公办学校学生享有同等权利。建立健全民办学校助学贷款业务扶持制度，提高民办学校家庭经济困难学生获得资助的比例。民办学校应当建立健全奖助学金评定、发放等管理机制，从学费收入中提取不少于</span><span style=\"font-family: Calibri;\">5%</span><span style=\"font-family: 宋体;\">的资金，用于奖励和资助学生。落实鼓励捐资助学的相关优惠政策措施，积极引导和鼓励企事业单位、社会组织和个人面向民办学校设立奖助学金，加大资助力度。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（三）加强教师队伍建设</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　将民办学校教师队伍建设纳入教师队伍建设整体规划。民办学校要着力加强教师思想政治工作，建立健全教育、宣传、考核、监督与奖惩相结合的师德建设长效机制，全面提升教师师德素养。加强辅导员、班主任队伍建设，促进人员数量与专业能力满足办学需求。进一步完善民办学校教师培养机制，充分发挥上海市师资培训中心、上海市民办高校教师专业发展中心等第三方教师专业培训机构的作用，提高民办学校教师教学科研水平，促进教师专业发展，引导民办学校根据自身特点，形成分层分级的校本研修机制。民办学校要在学费收入中，安排一定比例资金用于教师培训。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（四）完善教职工社会保障机制</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　完善学校、个人、政府合理分担的民办学校教职工社会保障机制。民办学校应当依法为教职工足额缴纳社会保险费和住房公积金。鼓励民办学校通过建立年金制度、购买商业保险等补充养老保险方式，改善教职工退休后的待遇，试点对距退休时间较短的专职教师加速年金积累。对实施全日制学历教育的民办学校，将年金制度的建立与落实情况作为拨付民办教育专项资金的重要因素之一。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（五）维护师生合法权益</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　依法落实民办学校师生对学校办学管理的知情权、参与权，保障师生参与民主管理和民主监督的权利。完善民办学校师生争议处理机制，完善教职工代表大会和学生代表大会制度，尊重、维护和保障教师、学生的合法权益。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　<strong>　十、提升管理水平</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><strong><span style=\"font-family: 宋体;\">　　（一）改进管理方式</span></strong></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　积极转变职能，减少事前审批，加强事中事后监管，提高政府管理服务水平。进一步清理涉及民办教育的行政许可事项，向社会公布权力清单、责任清单，严禁法外设权。改进许可方式，简化许可流程，明确工作时限，规范行政许可工作。进一步健全完善民办教育管理系统，逐步实现以大数据为基础的审批备案、监督检查、统计分析、信用管理、风险预警、信息公开、社会服务等功能，探索建立审批与备案事项并联办理机制，提升管理效能。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（二）完善年检年报制度</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　加强民办教育管理机制建设，强化民办教育督导，主管部门与法人登记部门加强合作，完善民办学校年度检查和年度报告制度。对民办学校党组织建设、党对民办学校的领导、办学指导思想、办学条件、办学形式、办学层次、专业设置、课程教材、招生规模、教育教学质量、依法办学、资产财务管理、校园安全稳定、消防安全等方面的情况进行年度检查，引导学校加强自身建设，提高办学质量。年度检查的结果，通过政府网站向社会公示。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（三）加强风险防范</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　加强对新设立民办学校举办者的资格审查。完善民办学校财务会计制度、内部控制制度、审计监督制度，加强风险防范。推进民办教育信息公开，建立民办学校信息强制公开制度。建立违规失信惩戒机制，将违规办学的学校及其举办者和负责人纳入“黑名单”，规范学校办学行为。对民办学校获得办学许可的情况，日常监督检查的情况，办学水平和质量进行评估的情况，年度资产财务状况、年度检查的结果以及对其违法行为进行处罚的情况，均应当通过学校网站、信息公告栏、电子屏幕等场所和设施公开，并可根据需要，设置公共阅览室、资料索取点方便调取和查阅。除学校已经公开的信息外，社会组织或者个人可以书面形式，向学校申请获取其他信息。探索建立民办学校、受教育者（家长）、保险公司共同参与的风险防范机制，完善学校重大责任事故处理和学校终止时善后事宜处理机制。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（四）建立综合监管机制</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　建立市、区、街镇三级联动的综合治理体系，健全联合执法机制，加大对违法违规办学行为的查处力度。教育、人力资源社会保障部门要加强行业管理，会同工商、民政、公安等有关部门和镇（乡）政府、街道办事处，形成巡查发现、受理分派、违法查处、检查督导、信息共享等各环节分工牵头负责、共同协作的机制，构建行政审批、登记注册、行业主管、行政执法相互衔接的综合监管机制。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>十一、发挥各方作用</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><strong><span style=\"font-family: 宋体;\">　　（一）完善政府协调机制</span></strong></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　将发展民办教育纳入当地经济社会发展和教育事业整体规划，加强战略研究、标准制定、政策实施等工作，积极推进民办教育改革发展。建立完善由市、区教育部门牵头，编制、发展改革、商务、公安、民政、财政、人力资源社会保障、规划国土资源、住房城乡建设管理、税务、工商、金融、人民银行上海分行、银监、证监等部门参加的市、区民办教育工作联席会议，建立健全管理体制，完善多部门协调机制，综合解决社会力量办学中的体制机制障碍，研究破解制约民办教育发展重大问题。发挥“上海市民办教育工作会议”和“上海市民办高校党建工作会议”功能，推进落实民办教育相关政策。将鼓励支持社会力量兴办教育作为考核各级政府改进公共服务方式的重要内容。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（二）发挥社会组织作用</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　积极培育民办教育行业组织，支持上海市民办教育协会、上海市民办教育发展服务中心等开展工作，支持行业组织在行业自律、交流合作、协同创新、履行社会责任等方面发挥桥梁和纽带作用。探索建立民办学校第三方质量认证制度和质量监控制度，培育更多的社会机构参与民办学校办学过程和办学质量评估。第三方评估结果，可以作为业务管理部门对民办学校予以奖励、警告、限期整改直至取消办学资格的参考之一。充分发挥各类机构在民办学校评估认证、咨询服务、风险防范、融资贷款等方面的作用。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（三）加强舆论宣传引导</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　各部门、各单位要认真贯彻落实《若干意见》精神，解放思想、凝聚共识、深化改革，制定具体措施，深入推进民办教育综合改革，总结体制机制改革的成功做法和先进经验，促进上海民办教育持续健康发展。加大对民办教育的宣传力度，按照国家有关规定，对民办教育改革发展作出突出贡献的集体和个人予以奖励和表彰，树立民办教育良好社会形象，努力营造全社会关心、共同支持社会力量兴办教育的良好氛围。</span></p><p style=\"line-height: 29px; text-indent: 28px; white-space: normal;\"><span style=\"font-family: 宋体;\">本实施意见自</span><span style=\"font-family: Calibri;\">2018</span><span style=\"font-family: 宋体;\">年</span><span style=\"font-family: Calibri;\">1</span><span style=\"font-family: 宋体;\">月</span><span style=\"font-family: Calibri;\">1</span><span style=\"font-family: 宋体;\">日起实施，有效期至</span><span style=\"font-family: Calibri;\">2027</span><span style=\"font-family: 宋体;\">年</span><span style=\"font-family: Calibri;\">12</span><span style=\"font-family: 宋体;\">月</span><span style=\"font-family: Calibri;\">31</span><span style=\"font-family: 宋体;\">日。</span></p><p style=\"line-height: 29px; text-indent: 28px; white-space: normal;\"><span style=\"font-family: Calibri;\">&nbsp;</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>附件：</strong></span><img style=\"margin-right: 2px; vertical-align: middle;\" src=\"http://118.31.3.95:8080/shtyxh/lib/extjs/ueditor/utf8-jsp/dialogs/attachment/fileTypeImages/icon_doc.gif\"/><a title=\"2.附件-本市各相关部门贯彻《上海市人民政府关于促进民办教育健康发展的实施意见》任务分工方案.docx\" style=\"color: rgb(0, 102, 204); font-size: 12px;\" href=\"http://118.31.3.95:8080/shtyxh/ueditor/jsp/upload/file/20180726/1532620093174042467.docx\">2.附件-本市各相关部门贯彻《上海市人民政府关于促进民办教育健康发展的实施意见》任务分工方案.docx</a></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\"></span><br/></p><p style=\"line-height: 29px; white-space: normal;\">　　<span style=\"font-family: Calibri;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p style=\"text-align: right; line-height: 29px; white-space: normal;\"><span style=\"font-family: Calibri;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family: 宋体;\">上海市人民政府</span></p><p style=\"text-align: right; line-height: 29px; white-space: normal;\">　　<span style=\"font-family: Calibri;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2017</span><span style=\"font-family: 宋体;\">年</span><span style=\"font-family: Calibri;\">12</span><span style=\"font-family: 宋体;\">月</span><span style=\"font-family: Calibri;\">26</span><span style=\"font-family: 宋体;\">日</span></p><p><span style=\"font-family: 宋体;\"><br/></span></p><p><br/></p>',50,'N','2018-08-22 09:25:29',0,3,-1,-1,-1,'2018-08-22 09:25:29',-1,'2018-09-30 11:30:50',-1,'ID',NULL,NULL,NULL),
 (1063,17,1,'','上海市人民政府关于印发《上海市民办学校分类许可登记管理办法》的通知','上海市民办学校分类许可登记管理办法','',NULL,'<p style=\"text-align: center; line-height: 29px; white-space: normal;\"><strong><span style=\"font-family: 微软雅黑, sans-serif;\">上海市人民政府关于印发《上海市民办学校分类许可登记管理办法》的通知</span></strong></p><p style=\"text-align: center; line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">沪府发〔</span>2017<span style=\"font-family: 宋体;\">〕</span>95<span style=\"font-family: 宋体;\">号</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">&nbsp;</span></p><p style=\"line-height: 29px; text-indent: 28px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">各区人民政府，市政府各委、办、局：</span></p><p style=\"line-height: 29px; text-indent: 28px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　现将《上海市民办学校分类许可登记管理办法》印发给你们，请认真按照执行。</span></p><p style=\"text-align: right; line-height: 29px; text-indent: 28px; margin-top: 0px; margin-right: 42px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: right; line-height: 29px; text-indent: 28px; margin-top: 0px; margin-right: 42px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">上海市人民政府</span></p><p style=\"text-align: right; line-height: 29px; text-indent: 28px; margin-top: 0px; margin-right: 28px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">2017</span><span style=\"font-size: 14px;\">年</span><span style=\"font-size: 14px;\">12</span><span style=\"font-size: 14px;\">月</span><span style=\"font-size: 14px;\">26</span><span style=\"font-size: 14px;\">日</span></p><p style=\"text-align: right; text-indent: 32px; white-space: normal;\">&nbsp;</p><p style=\"text-align: center; white-space: normal;\"><strong><span style=\"font-family: 微软雅黑, sans-serif; font-size: 19px;\"></span></strong></p><hr style=\"white-space: normal;\"/><p style=\"text-align: center; white-space: normal;\"><strong><span style=\"font-family: 微软雅黑, sans-serif; font-size: 19px;\"><br/></span></strong></p><p style=\"text-align: center; white-space: normal;\"><strong><span style=\"font-family: 微软雅黑, sans-serif; font-size: 19px;\">上海市民办学校分类许可登记管理办法</span></strong><br/></p><p style=\"text-align: center; line-height: 29px; margin-bottom: 0px; white-space: normal;\"><strong><span style=\"font-family: 黑体; font-size: 14px;\">第一章　总则</span></strong></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第一条（制定依据）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　依据《全国人民代表大会常务委员会关于修改〈中华人民共和国民办教育促进法〉的决定》《中华人民共和国民办教育促进法》《国务院关于鼓励社会力量兴办教育促进民办教育健康发展的若干意见》《关于加强民办学校党的建设工作的意见（试行）》《民办学校分类登记实施细则》《营利性民办学校监督管理实施细则》等法律、法规、规章和政策，结合本市实际，制定本办法。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第二条（基本原则）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　落实国家关于鼓励社会力量兴办教育、推进民办教育分类管理改革的精神，健全本市民办教育相关法律法规，规范民办学校办学行为，促进民办教育健康发展，服务地方经济和社会发展。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　市和各区政府相关职能部门应当依法实行民办学校分类管理，按照“公开、公平、公正、便民”的要求，为民办学校提供指导与服务。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第三条（适用范围）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　在本市行政区域内，由国家机构以外的组织或者个人，利用非国家财政性经费，面向社会举办学校及其他教育机构（以下统称“民办学校”）的活动，适用本办法。本办法未作规定的，依照有关法律、法规、规章、政策执行。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第四条（管理权限）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　市教育部门应当做好本市民办教育工作的统筹规划、综合协调、宏观管理和政策制定等工作，人力资源社会保障、民政、工商及其他有关部门在各自的职责范围内，分别负责有关的民办教育工作。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　市和各区教育、人力资源社会保障部门是相应民办学校的许可机关和主管部门（以下统称“许可机关”），负责对民办学校实施规划和许可。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　市和各区民政、工商（市场监督管理）等部门是相应民办学校的登记机关（以下统称“登记机关”），负责民办学校的法人登记。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　市、区各职能部门分别依职权对各民办学校依法予以监督管理。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第五条（基本流程）</span></p><p style=\"line-height: 29px; text-indent: 28px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">设立民办学校，应当依据相关法律法规规定，经许可机关批准筹设或者正式设立，取得办学许可证后，到相应的登记机关办理法人登记手续。</span></p><p style=\"line-height: 29px; text-indent: 28px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: center; line-height: 29px; margin-bottom: 0px; white-space: normal;\"><strong><span style=\"font-family: 黑体; font-size: 14px;\">第二章　设立</span></strong></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第六条（设立原则）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　设立民办学校，应当符合国家和本市社会经济和教育事业发展的需要，具备相应的条件，并且符合本市相应法人登记管理的规定。举办者可以自主选择设立非营利性或者营利性民办学校。但是，不得设立实施义务教育的营利性民办学校。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　非营利性民办学校的举办者不得取得办学收益，学校的办学结余全部用于办学。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　营利性民办学校的举办者可以取得办学收益，学校的办学结余依照公司法等有关法律、法规的规定处理。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第七条（审批权限）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　设立实施学前教育、义务教育、高级中等教育、中等及以下职业技术学历教育以及以文化培训为主的民办学校，由学校所在地的区教育部门审批，并报市教育部门备案。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　设立实施高等学历教育的民办学校，由市教育部门统一受理，报市政府审批，然后报国家教育部门备案。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　设立实施以职业技能为主的职业资格培训、职业技能培训的民办学校，由区人力资源社会保障部门按照相关法律法规的规定审批，报市人力资源社会保障部门备案，并抄送同级教育部门备案。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　法律法规对审批权限另有规定的，从其规定。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第八条（设立条件）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　国家机构以外的组织或者个人，具备与拟举办民办学校的层次、类型、规模相适应的经济实力的，可以单独或者联合举办民办学校。联合举办民办学校的，应当依法签订联合办学协议。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　申请举办民办学校的组织，应当具有法人资格，信用状况良好。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　申请举办民办学校的个人，应当具有中华人民共和国国籍，信用状况良好，具有政治权利和完全民事行为能力。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　举办者可以用货币出资，也可以用实物、知识产权、土地使用权等可以用货币估价并可以依法转让的非货币财产作价作为办学出资，并且应当符合法人登记的相关规定，但法律法规规定不得作为出资的财产除外。其中，举办者以国有资产参与举办民办学校的，应当根据有关国有资产监督管理的规定，依法办理相关手续；属外商投资的，应当按照《中华人民共和国中外合作办学条例》《外商投资产业指导目录》等国家和本市规定，依法办理相关手续。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校应当具备法人条件，符合与办学内容和规模等相应的设置标准，并且应当符合法人登记的相关条件。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第九条（学校名称）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校的名称应当符合法人登记的相关规定。民办学校只能使用一个名称，由行政区划名称、字号、办学层次和类别等部分依次组成。民办学校的名称不得损害社会公共利益，不得违背社会道德风尚，并且外文名称应当与中文名称言义一致。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　其中，营利性民办学校的名称还应当包含“有限责任公司”或者“股份有限公司”等公司组织形式部分。实施学历教育的民办学校和民办幼儿园在申请筹设或者正式设立时，可以向许可机关申请使用简称，并由许可机关在筹设批准书或者办学许可证上予以注明。简称仅可省略学校的公司组织形式，并仅限用于学校牌匾、成绩单、学位证书、学历证书、招生广告和简章。在招生广告和简章中使用办学简称的，应当在显著位置注明学校营利性属性，并在学校介绍中标注学校全称。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　本办法实施前已经设立登记为民办非企业单位法人的民办学校，继续作为非营利性民办学校并且学校名称不变更的，可以继续沿用原有名称。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第十条（学校筹设）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　（一）（筹设申请）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　申请筹设民办学校的，在经登记机关确定名称后，由举办者向许可机关提出书面申请，根据相关规定提交申请材料，申请人对申请材料的真实、完整负责。申请材料主要内容包括：</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　学校名称、举办者资质、培养目标、办学规模、办学层次、办学形式、办学条件、办学内容、内部管理体制、经费筹措与管理使用、资产信息及有效证明、党组织建设材料以及法律法规规定的其他材料。其中，属捐赠性质的财产须提交捐赠协议及相关有效证明文件；联合举办民办学校的须提交联合办学协议。民办学校举办者再申请举办营利性民办学校的，还应当按照《营利性民办学校监督管理实施细则》的规定，提交相关材料。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　（二）（申请受理）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　许可机关收到举办者提出的筹设民办学校的申请后，应当依法对申请材料进行审核，并根据下列情况分别作出处理：</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\">　　<span style=\"font-size: 14px;\">1.</span><span style=\"font-size: 14px;\">申请事项依法不属于本部门职权范围的，告知申请人向有关行政部门申请；</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\">　　<span style=\"font-size: 14px;\">2.</span><span style=\"font-size: 14px;\">申请材料不齐全或者不符合法定形式的，当场或者在</span><span style=\"font-size: 14px;\">5</span><span style=\"font-size: 14px;\">个工作日内，一次告知申请人需要补正的全部内容；</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\">　　<span style=\"font-size: 14px;\">3.</span><span style=\"font-size: 14px;\">申请材料齐全、符合法定形式的，或者申请人按照主管部门的要求提交全部补正申请材料的，主管部门予以受理。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　（三）（审核批准）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　受理申请的，应当自受理筹设申请之日起的</span><span style=\"font-size: 14px;\">30</span><span style=\"font-size: 14px;\">日内，以书面形式作出是否同意的决定。许可机关同意筹设的，发给筹设批准书；不同意筹设的，应当书面说明理由，并告知救济途径。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　（四）（筹设期限）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校的举办者在获得筹设批准书之日起</span><span style=\"font-size: 14px;\">3</span><span style=\"font-size: 14px;\">年内完成筹设的，可以提出正式设立申请。超过</span><span style=\"font-size: 14px;\">3</span><span style=\"font-size: 14px;\">年的，举办者应当重新申报。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第十一条（正式设立）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　（一）（设立申请）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　申请正式设立民办学校的，在经登记机关确定名称后，由举办者向许可机关提出书面申请，根据相关规定和筹设实际情况，提交本办法第十条第（一）项规定的更新后的材料，并且还应当提交以下材料：正式设立申请报告，筹设批准材料与筹设情况报告，学校章程，学校办学场所及资金资产的有效证明文件，股东或者股东会、首届董事会或者理事会、监事或者监事会等组成人员名单及其身份证明文件，校长等教职工的资格证明文件等。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　已具备办学条件、达到相关设置标准的，举办者可以直接申请正式设立民办学校，并提交除筹设批准书以外的其他申请材料。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　（二）（申请受理）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　许可机关收到举办者提出的正式设立民办学校的申请后，应当依法对申请材料进行审核，并根据下列情况分别作出处理：</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\">　　<span style=\"font-size: 14px;\">1.</span><span style=\"font-size: 14px;\">申请事项依法不属于本部门职权范围的，告知申请人向有关行政部门申请；</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\">　　<span style=\"font-size: 14px;\">2.</span><span style=\"font-size: 14px;\">申请材料不齐全或者不符合法定形式的，当场或者在</span><span style=\"font-size: 14px;\">5</span><span style=\"font-size: 14px;\">个工作日内一次告知申请人需要补正的全部内容；</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\">　　<span style=\"font-size: 14px;\">3.</span><span style=\"font-size: 14px;\">申请材料齐全、符合法定形式的，或者申请人按照主管部门的要求提交全部补正申请材料的，主管部门予以受理。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　（三）（审核批准）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　许可机关受理正式设立民办学校的申请后，应当组织专家或者委托具有相应资质的教育评估机构，对申请材料、实际办学条件和办学能力进行审核评议或者评估论证，由专家或者评估机构出具书面报告。审核评议或者评估论证的费用由许可机关承担。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　许可机关应当依照相关法律法规和本办法的规定，自受理之日起</span><span style=\"font-size: 14px;\">3</span><span style=\"font-size: 14px;\">个月内，以书面形式作出是否批准正式设立民办学校的决定。其中，申请正式设立民办高等学校的，许可机关应当自受理之日起</span><span style=\"font-size: 14px;\">6</span><span style=\"font-size: 14px;\">个月内，以书面形式作出是否批准的决定。对不批准的，应当书面说明理由，并告知救济途径。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　（四）（发证备案）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　许可机关对批准设立的民办学校，根据学校属性颁发办学许可证。其中，人力资源社会保障部门应当将批准情况抄送同级教育部门备案，区教育部门应当将批准材料报送市教育部门备案，市教育部门应当将民办高等学校的审批情况报送国家教育部门备案。各许可机关应当将批准设立的民办学校的名称、地址、校长、层次、类别、属性、办学内容等信息，通过政府网站等媒体向社会公开。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第十二条（法人登记）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校取得办学许可证后，按照相关法律法规规定，根据办学层次、类别和属性等到相应的登记机关，申请法人登记。登记机关对符合条件的民办学校，依法予以法人登记，并核发登记证或者营业执照等证件；对不符合登记条件的，不予登记，并以书面形式向申请人说明理由。民办学校在筹设期内或者取得办学许可证但未完成法人登记的，不得以任何名义开展招生与教育教学活动。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　批准设立的非营利性民办学校，符合国家和本市有关规定的，可以到民政等部门办理法人登记手续；批准设立的营利性民办学校到工商（市场监督管理）部门办理法人登记手续。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第十三条（证件管理）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校依法获得办学许可和法人登记的相关证件，依法按照证件注明的办学内容和业务范围开展教育教学活动。证件必须在民办学校公开场所的显著位置公示。遗失证件的应当立即公告，并及时向证件颁发机关补办。不得以任何名义出租出借相关证件。</span></p><p style=\"line-height: 29px; text-indent: 28px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">需要延续办学许可有效期的，民办学校应当在办学许可有效期届满</span><span style=\"font-size: 14px;\">30</span><span style=\"font-size: 14px;\">个工作日前，向相应许可机关提出申请。许可机关应当根据申请，在其办学许可有效期届满前，作出是否准予延续的决定；逾期未作决定的，视为准予延续。办学许可有效期届满未延续的，许可机关应当依法办理注销手续。</span></p><p style=\"line-height: 29px; text-indent: 28px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: center; line-height: 29px; margin-bottom: 0px; white-space: normal;\">　　<strong><span style=\"font-family: 黑体; font-size: 14px;\">第三章　组织机构</span></strong></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第十四条（党组织）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　各级党组织应当健全民办学校党组织设置，完善党组织工作保障机制，发挥党组织的政治核心作用。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　各级党组织应当选好管好民办学校党组织书记。其中，民办高等学校的党组织书记兼任政府督导专员。加强对民办学校党建工作的领导，把党建工作情况作为民办学校许可登记、年检年审、评估考核、管理监督的必备条件和必查内容。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校应当建立健全党组织参与决策和监督机制，把党组织建设有关内容纳入学校章程。推进党组织班子成员进入学校决策层和行政管理层，党组织书记应当通过法定程序进入学校理事会或者董事会。党组织班子成员应当按照学校章程进入行政管理层，党员校长、副校长等行政领导班子成员，可以按照党内有关规定进入党组织班子。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第十五条（理事会和董事会）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校应当按照相关法律法规和章程的规定，设立理事会、董事会或者其他形式的决策机构。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校的理事会或者董事会构成、成员数量、成员资质、产生程序和职权等应当符合相关法律法规和学校章程的规定。其成员由举办者或者其代表、校长、党组织负责人、教职工代表等人员组成，可以吸纳教育专家和社会知名人士参加。首届理事会或者董事会成员及其职务由举办者推选，需要变更的，应当按照本办法第二十五条第二款的规定办理。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校理事会或者董事会的议事规则应当在学校章程中依法予以规定，明确会议召集与召开方式、表决形式、讨论事项的通过要求、重大事项的确定方法等内容。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第十六条（法定代表人）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校的法定代表人，由理事长、董事长或者校长担任，并应当符合相关法律法规和学校章程的规定。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第十七条（校长）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校的理事会或者董事会应当依法聘任专职校长，校长对理事会或者董事会负责。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　校长应当具有中华人民共和国国籍，具有政治权利和完全民事行为能力，在中国境内定居，个人信用状况良好，熟悉教育及相关法律法规，年龄一般不超过</span><span style=\"font-size: 14px;\">70</span><span style=\"font-size: 14px;\">岁，具有</span><span style=\"font-size: 14px;\">5</span><span style=\"font-size: 14px;\">年以上教育管理经验和相关法律法规及设置标准规定的其他任职条件。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　校长依据法律法规和学校章程的规定，独立行使教育教学和行政管理职权。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第十八条（监事制度）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校应当设立监事或者监事会，其人选及构成、产生办法、任期、基本职责和议事规则等应当符合相关法律法规和学校章程的规定。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第十九条（群众组织）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校应当积极支持工会、共青团组织、妇女联合会、教职工（代表）大会、学生代表大会等群众组织依法建立和开展活动，保障其依法、有序、广泛地参与学校的民主管理和监督。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第二十条（其他内部管理事项）</span></p><p style=\"line-height: 29px; text-indent: 28px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">民办学校应当依法制定学校章程和各项内部管理制度。营利性民办学校还应当依法设立股东会或者股东大会，并履行相应的法定职责。有犯罪记录、无民事行为能力或者限制民事行为能力的，不得在学校理事会或者董事会、监事会和行政机构任职。一个自然人不得兼任同一所学校的理事（董事）和监事。</span></p><p style=\"line-height: 29px; text-indent: 28px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: center; line-height: 29px; margin-bottom: 0px; white-space: normal;\">　　<strong><span style=\"font-family: 黑体; font-size: 14px;\">第四章　变更与终止</span></strong></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第二十一条（举办者变更）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校的举办者变更的，由现举办者提出，在进行财务清算后，经理事会或者董事会同意，由民办学校向许可机关提出书面申请，并提交申请材料。其中，营利性民办学校的举办者继承人、财产析得人或者受赠人因继承、析产或者赠与获得营利性民办学校举办者权益的，应当依法办理举办者变更手续。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　许可机关应当依法对民办学校的举办者变更进行审批，对同意变更的颁发新的办学许可证，并且收回原办学许可证。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校举办者的姓名或者名称变更的，应当在</span><span style=\"font-size: 14px;\">30</span><span style=\"font-size: 14px;\">日内报许可机关备案、申请换发办学许可证。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校在取得新的办学许可证后，应当依法及时办理法人登记等相关手续。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第二十二条（分立、合并）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校的分立、合并，应当进行财务清算，在理事会或者董事会作出决议后，向许可机关提出书面申请，其中营利性民办学校还应当符合公司法等相关法律法规规定；涉及举办者变更的，应当根据本办法第二十一条规定办理相应手续。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　申请分立、合并民办学校的，许可机关应当自受理之日起</span><span style=\"font-size: 14px;\">3</span><span style=\"font-size: 14px;\">个月内以书面形式答复；其中，申请分立、合并民办高等学校的，许可机关应当自受理之日起</span><span style=\"font-size: 14px;\">6</span><span style=\"font-size: 14px;\">个月内以书面形式答复。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　经审批同意分立或者合并的，许可机关向民办学校颁发新的办学许可证，并收回原办学许可证。民办学校应当依法及时办理法人登记等相关手续。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　分立、合并后的民办学校，应当符合相应的设置标准。民办学校不得分立为不同办学属性的学校，不同办学属性的学校不得合并。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第二十三条（层次、类别变更）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校变更办学层次、类别的，应当在理事会或者董事会作出决议后，向许可机关提出书面申请。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　许可机关应当依法对民办学校的变更申请进行审批，自受理之日起</span><span style=\"font-size: 14px;\">3</span><span style=\"font-size: 14px;\">个月内以书面形式答复；其中，申请变更为民办高等学校的，许可机关应当自受理之日起</span><span style=\"font-size: 14px;\">6</span><span style=\"font-size: 14px;\">个月内以书面形式答复。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　经审批同意变更的，许可机关向民办学校颁发新的办学许可证，并收回原办学许可证。民办学校应当依法及时办理法人登记等相关手续。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　办学层次、类别变更后的民办学校，应当符合相应的设置标准。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第二十四条（场所变更）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校应当在经许可机关审批同意的场所开展教育教学活动。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校变更办学地址的，应当向许可机关提出书面申请。经审批同意变更的，许可机关向民办学校颁发新的办学许可证或者其他证明文件。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校办学地址变更涉及变更法人登记的，还应当在取得新的办学许可证或者其他证明文件后，依法及时办理法人登记等相关手续。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校的办学场所，应当符合相应的设置标准。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第二十五条（其他变更）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　名称变更：民办学校需要变更名称的，由理事会或者董事会作出决议，经登记机关确定名称后，由民办学校向许可机关提出申请。经审批同意变更的，许可机关向民办学校颁发新的办学许可证，并收回原办学许可证。民办学校应当在取得新的办学许可证后，依法及时办理法人登记等相关手续。名称变更后的民办学校，应当符合相应的设置标准。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　理事或者董事变更：民办学校的理事会或者董事会在任期届满、推选方更换推选人员、成员资质条件发生变化等情况下，应当及时换届，或者开展成员及其职务的变更工作，并且由在任理事会或者董事会作出决议后的</span><span style=\"font-size: 14px;\">30</span><span style=\"font-size: 14px;\">日内，将变更后的成员名单及其职务报许可机关和登记机关备案。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　校长变更：民办学校在校长任期届满、任期未满但任职条件发生变化等情况下，学校理事会或者董事会应当依法及时开展校长的解聘与聘任工作，符合学校需要并且具有相应任职条件的可以续聘。民办学校应当在理事会或者董事会作出校长聘任或者续聘决议后的</span><span style=\"font-size: 14px;\">30</span><span style=\"font-size: 14px;\">日内，向许可机关申请颁发新的办学许可证。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　法定代表人变更：民办学校需要变更法定代表人的，应当依法开展经济责任审计，并且在理事会或者董事会作出变更决议后的</span><span style=\"font-size: 14px;\">30</span><span style=\"font-size: 14px;\">日内，依法办理法人登记等相关手续。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　监事变更：民办学校需要变更监事的，应当按照相关法律法规与学校章程的规定，由相关各方推选人员，相关材料应当存档备查。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　章程修订：民办学校章程的修订，应当在理事会或者董事会作出修订决议后，按照相关法律法规的规定办理核准与备案手续。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第二十六条（信息变更）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校的变更事项经审批同意后，民办学校与许可机关应当及时更新民办教育管理系统中的相关信息，并且应当依法做好信息公开工作。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第二十七条（终止）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校应当在法律法规和学校章程规定的情形下终止。民办学校终止时，应当做好师生安置、财务清算和财产清偿以及安全稳定等工作。其中，实施义务教育的民办学校终止时，教育部门应当协助学校安排学生继续就学。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第二十八条（资产处置）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校终止时，应当依法进行财务清算。民办学校自己要求终止的，由民办学校组织清算；被许可机关依法撤销的，由许可机关组织清算；因资不抵债无法继续办学而被终止的，由人民法院组织清算。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校依法清偿相关债务后的剩余财产，按照有关规定和学校章程处理。其中，非营利性民办学校清偿债务后的剩余财产应当按照学校章程的规定或者理事会、董事会的决议用于其他非营利性民办学校办学，不得向举办者分配剩余财产；无法按照学校章程的规定或者理事会、董事会的决议处理的，由许可机关主持转给其他非营利性民办学校，并且向社会公告。营利性民办学校清偿债务后的剩余财产，依照公司法等有关规定处理。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第二十九条（注销程序）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　民办学校终止应当及时办理注销办学许可和法人登记等手续，将办学许可证、法人登记证书和印章等分别交回许可机关和登记机关。</span></p><p style=\"line-height: 29px; text-indent: 28px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">许可机关应当将准予民办学校终止或者注销办学许可的决定告知登记机关，并通过政府网站等媒体向社会公告。同时，区教育部门应当将相关决定报市教育部门备案，区人力资源社会保障部门应当将相关决定告知同级教育部门。</span></p><p style=\"line-height: 29px; text-indent: 28px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: center; line-height: 29px; margin-bottom: 0px; white-space: normal;\"><strong><span style=\"font-family: 黑体; font-size: 14px;\">第五章　已设学校</span></strong></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第三十条（登记为民办非企业单位法人的现有学校的过渡安排）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\">　　<span style=\"font-size: 14px;\">2016</span><span style=\"font-size: 14px;\">年</span><span style=\"font-size: 14px;\">11</span><span style=\"font-size: 14px;\">月</span><span style=\"font-size: 14px;\">7</span><span style=\"font-size: 14px;\">日前设立的登记为民办非企业单位法人的现有民办学校，其举办者可以自主选择举办非营利性民办学校或者营利性民办学校。举办者应当在</span><span style=\"font-size: 14px;\">2018</span><span style=\"font-size: 14px;\">年</span><span style=\"font-size: 14px;\">12</span><span style=\"font-size: 14px;\">月</span><span style=\"font-size: 14px;\">31</span><span style=\"font-size: 14px;\">日前，向许可机关提交关于学校办学属性选择的书面材料，未按期提交材料的学校不得转设为营利性民办学校。其中，实施义务教育的学校，不得登记为营利性民办学校。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　选择登记为非营利性民办学校的，应当在</span><span style=\"font-size: 14px;\">2019</span><span style=\"font-size: 14px;\">年</span><span style=\"font-size: 14px;\">12</span><span style=\"font-size: 14px;\">月</span><span style=\"font-size: 14px;\">31</span><span style=\"font-size: 14px;\">日前，依法修订学校章程、完善法人治理结构和内部管理制度、继续办学。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　选择登记为营利性民办学校的，应当在许可机关以及相关职能部门的指导下，由学校组织进行财务清算，依法明确资产权属，按照国家有关规定缴纳相关税费，重新办理法人登记手续，继续办学。经清算确认的举办者的出资应当为重新登记后法人的注册资本和实缴资本，除财政投入、社会捐赠等按照相关规定处理外，经清算确认的所有资产及其相关权利义务由重新登记后的法人承继；符合条件的，依法享受相关税费优惠政策。其中，主要实施高等学历教育的学校，应当在</span><span style=\"font-size: 14px;\">2021</span><span style=\"font-size: 14px;\">年</span><span style=\"font-size: 14px;\">12</span><span style=\"font-size: 14px;\">月</span><span style=\"font-size: 14px;\">31</span><span style=\"font-size: 14px;\">日前完成上述工作；其他学校应当在</span><span style=\"font-size: 14px;\">2020</span><span style=\"font-size: 14px;\">年</span><span style=\"font-size: 14px;\">12</span><span style=\"font-size: 14px;\">月</span><span style=\"font-size: 14px;\">31</span><span style=\"font-size: 14px;\">日前完成上述工作。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　市、各区政府相关部门根据各民办学校举办者提交的书面材料，以及基于该选择后续相关工作的开展情况等，予以差异化的扶持和管理。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第三十一条（经营性民办培训机构的过渡安排）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　本办法实施前设立的经营性民办培训机构，应当按照相关法律法规的规定修订章程、健全法人治理结构、完善办学条件，向住所所在地的相应许可机关申请取得办学许可证，在取得办学许可证后及时依法办理其他相关手续。以上工作应当在</span><span style=\"font-size: 14px;\">2019</span><span style=\"font-size: 14px;\">年</span><span style=\"font-size: 14px;\">12</span><span style=\"font-size: 14px;\">月</span><span style=\"font-size: 14px;\">31</span><span style=\"font-size: 14px;\">日前完成。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第三十二条（登记为民办非企业单位法人的现有学校的补偿奖励）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\">　　<span style=\"font-size: 14px;\">2016</span><span style=\"font-size: 14px;\">年</span><span style=\"font-size: 14px;\">11</span><span style=\"font-size: 14px;\">月</span><span style=\"font-size: 14px;\">7</span><span style=\"font-size: 14px;\">日前设立的登记为民办非企业单位法人的现有民办学校选择登记为非营利性民办学校后终止，或者未及选择直接终止，妥善安置学生和教职工，并且规范开展相关工作的，根据出资者的申请，由许可机关会同相关职能部门综合考虑其在</span><span style=\"font-size: 14px;\">2017</span><span style=\"font-size: 14px;\">年</span><span style=\"font-size: 14px;\">9</span><span style=\"font-size: 14px;\">月</span><span style=\"font-size: 14px;\">1</span><span style=\"font-size: 14px;\">日前的出资、取得合理回报的情况以及办学效益等因素，从学校依法清偿后的剩余财产中给予出资者相应的补偿或者奖励，补偿与奖励的金额按照本办法所附方法计算。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　补偿与奖励从学校剩余财产中的货币资金提取，货币资金不足的，从将其他资产依法转让后获得的货币资金中提取。剩余财产在扣除对出资者的补偿和奖励后，其余财产继续用于其他非营利性学校办学。</span></p><p style=\"line-height: 29px; text-indent: 28px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">办学许可或者法人登记被注销前</span><span style=\"font-size: 14px;\">2</span><span style=\"font-size: 14px;\">年年度检查连续不合格的，或者办学许可证或者法人登记证被吊销的民办学校，对其出资者不予奖励。</span></p><p style=\"line-height: 29px; text-indent: 28px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: center; line-height: 29px; margin-bottom: 0px; white-space: normal;\"><strong><span style=\"font-family: 黑体; font-size: 14px;\">第六章　监督与管理</span></strong></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第三十三条（信息公开）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　政府公开：各有关职能部门应当依法建立民办教育信息公开制度、信用档案制度、违规失信惩戒制度和预警制度，健全完善民办教育管理系统，向社会公开民办教育法规和文件、批准设立和终止的民办学校信息、民办学校年度检查情况、民办学校接受扶持奖励和受到的处罚情况等内容。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　学校公开：民办学校应当建立信息公开工作机制，通过学校网站、信息公告栏等，及时公开和更新学校基本信息、举办者基本信息、章程、规章制度、主要人员变更情况、招生考试录取办法、收取费用办法、学校资产和财务状况、应急事故处理等内容。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第三十四条（监督管理）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　市级有关部门应当做好对各区相关部门的指导与监督，市、各区相关职能部门应当分别依照法定职权做好对民办学校的监督管理，依法组织开展专项检查、日常检查、办学评估、年度检查、年度报告等工作。健全民办高等学校督导专员选聘与管理机制，充分发挥督导专员对于学校规范办学的引导与监督作用。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　教育督导部门依法建立健全民办学校督导制度，通过专项督导与综合督导等方式，对民办学校的办学行为实施监督、指导，对教育发展状况和教育质量组织开展评估、监测。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　教育部门等各有关部门应当依法建立对违法违规办学行为的投诉举报渠道，向社会公开投诉部门、联系电话，对投诉举报应当及时进行查处。各相关部门应当建立健全民办教育联合执法机制，对民办学校的违法违规办学行为依法予以处理。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第三十五条（行业自律）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　鼓励民办教育相关行业组织构建行业自律监管体系，建立行业内部规范管理制度、行业自律性约束机制和行业诚信制度。支持行业组织在服务行业、交流合作、协同创新、履行社会责任等方面发挥桥梁纽带作用，探索建立校际互助制度。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第三十六条（专业组织）</span></p><p style=\"line-height: 29px; text-indent: 28px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">鼓励和引导社会专业机构参与民办教育治理，为民办学校提供评估认证服务、咨询服务、法律服务、金融保险服务等。支持教育中介组织及其他行业组织在引导民办学校坚持公益性办学、创新人才培养模式、提升人才培养质量等方面发挥作用。</span></p><p style=\"line-height: 29px; text-indent: 28px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: center; line-height: 29px; margin-bottom: 0px; white-space: normal;\">　　<strong><span style=\"font-family: 黑体; font-size: 14px;\">第七章　法律责任</span></strong></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第三十七条（学校责任）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　擅自举办民办学校的，或者民办学校有违法违规行为的，由各相关部门依法予以查处；构成违反治安管理行为的，由公安机关依法给予治安管理处罚；构成犯罪的，依法追究刑事责任。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第三十八条（政府责任）</span></p><p style=\"line-height: 29px; text-indent: 28px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">各级相关职能部门在民办学校许可登记管理工作中有违法违规行为的，由上级机关责令其改正；情节严重的，对直接负责的主管人员和其他直接责任人员，依法给予处分；造成经济损失的，依法承担赔偿责任；构成犯罪的，依法追究刑事责任。</span></p><p style=\"line-height: 29px; text-indent: 28px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: center; line-height: 29px; margin-bottom: 0px; white-space: normal;\"><strong><span style=\"font-family: 黑体; font-size: 14px;\">第八章　附则</span></strong></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第三十九条（时限计算）</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　本办法规定的各类事项的期限，除明确以工作日计算的之外，均按照自然日或者顺延月、年计算，含法定节假日。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　第四十条（实施日期）</span></p><p style=\"line-height: 29px; text-indent: 28px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">本办法自</span><span style=\"font-size: 14px;\">2018</span><span style=\"font-size: 14px;\">年</span><span style=\"font-size: 14px;\">1</span><span style=\"font-size: 14px;\">月</span><span style=\"font-size: 14px;\">1</span><span style=\"font-size: 14px;\">日起实施，有效期至</span><span style=\"font-size: 14px;\">2027</span><span style=\"font-size: 14px;\">年</span><span style=\"font-size: 14px;\">12</span><span style=\"font-size: 14px;\">月</span><span style=\"font-size: 14px;\">31</span><span style=\"font-size: 14px;\">日。</span></p><p style=\"line-height: 29px; text-indent: 28px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">&nbsp;</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\">　　<strong><span style=\"font-family: 黑体; font-size: 14px;\">附件：补偿与奖励的计算方法</span></strong></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　《上海市民办学校分类许可登记管理办法》第三十二条规定的补偿与奖励金额按照如下方法计算：</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　一、补偿</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　补偿金额为出资金额与该出资的历年折算利息之和，在扣除出资者历年取得的合理回报与合理回报相应的历年折算利息后的金额，但不得超过剩余财产扣除财政扶持和社会捐赠形成资产后的金额。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　其中，折算利息分别按照出资时或者取得合理回报时，至学校停止办学或者办学许可失效的先至时间，同期一至三年期或者一至五年期金融机构人民币贷款基准利率和一年期金融机构人民币定期存款基准利率的平均值计算。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　二、奖励</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　奖励金额以学校停止办学或者办学许可失效的先至时间前</span><span style=\"font-size: 14px;\">5</span><span style=\"font-size: 14px;\">年内的最高年度学费总收入金额为基数，以</span><span style=\"font-size: 14px;\">2017</span><span style=\"font-size: 14px;\">年</span><span style=\"font-size: 14px;\">9</span><span style=\"font-size: 14px;\">月</span><span style=\"font-size: 14px;\">1</span><span style=\"font-size: 14px;\">日之后历年年度检查的结果为系数予以折算，奖励金额最高不超过清偿后的剩余财产扣除财政扶持和社会捐赠形成的资产以及补偿后的金额。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　其中，系数初始值和最低值为</span><span style=\"font-size: 14px;\">0</span><span style=\"font-size: 14px;\">，学校每获得一次年度检查“合格”的结论，系数增加</span><span style=\"font-size: 14px;\">0.1</span><span style=\"font-size: 14px;\">；每获得一次“不合格”的结论，系数扣除</span><span style=\"font-size: 14px;\">0.5</span><span style=\"font-size: 14px;\">。</span></p><p style=\"line-height: 29px; margin-bottom: 0px; white-space: normal;\"><span style=\"font-size: 14px;\">　　办学许可或者法人登记被注销前</span><span style=\"font-size: 14px;\">2</span><span style=\"font-size: 14px;\">年年度检查连续不合格的，或者办学许可证或者法人登记证被吊销的民办学校，对其出资者不予奖励。</span></p><p style=\"line-height: 29px; white-space: normal;\">&nbsp;</p><p><br/></p>',50,'N','2018-08-22 09:26:25',0,3,-1,-1,-1,'2018-08-22 09:26:25',-1,'2018-09-30 11:30:32',-1,'ID',NULL,NULL,NULL),
 (1064,17,1,'','上海市人民政府印发《关于促进和加强本市3岁以下幼儿托育服务工作的指导意见》的通知','关于促进和加强本市3岁以下幼儿托育服务工作的指导意见','',NULL,'<p style=\"text-align: center; white-space: normal;\"><strong><span style=\"font-family: 黑体; font-size: 20px;\">上海市人民政府印发《关于促进和加强本市3岁以下幼儿托育服务工作的指导意见》的通知</span></strong></p><p style=\"text-align: center; margin-top: 10px; white-space: normal;\"><strong><span style=\"font-family: 宋体;\">沪府发〔2018〕19号</span></strong></p><p style=\"text-align: center; margin-top: 10px; white-space: normal;\"><strong><span style=\"font-family: 宋体;\">&nbsp;</span></strong></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体; font-size: 19px;\">各区人民政府，市政府各委、办、局，各有关单位：</span></p><p style=\"line-height: 29px; text-indent: 37px; white-space: normal;\"><span style=\"font-family: 宋体; font-size: 19px;\">现将《关于促进和加强本市3岁以下幼儿托育服务工作的指导意见》印发给你们，请认真按照执行。</span></p><p style=\"line-height: 29px; text-indent: 37px; white-space: normal;\"><span style=\"font-family: 宋体; font-size: 19px;\">&nbsp;</span></p><p style=\"text-align: right; line-height: 29px; text-indent: 392px; white-space: normal;\"><span style=\"font-family: 宋体; font-size: 19px;\">上海市人民政府</span></p><p style=\"text-align: right; line-height: 29px; text-indent: 392px; white-space: normal;\"><span style=\"font-family: 宋体; font-size: 19px;\">2018</span><span style=\"font-family: 宋体; font-size: 19px;\">年4月27日</span></p><p style=\"text-align: center; margin-bottom: 21px; white-space: normal;\"><strong><span style=\"font-family: 黑体; font-size: 21px;\"></span></strong></p><hr style=\"white-space: normal;\"/><p style=\"text-align: center; margin-bottom: 21px; white-space: normal;\"><br/></p><p style=\"text-align: center; margin-bottom: 21px; white-space: normal;\"><br/></p><p style=\"text-align: center; margin-bottom: 21px; white-space: normal;\"><strong><span style=\"font-family: 黑体; font-size: 21px;\">关于促进和加强本市3岁以下幼儿托育服务工作的指导意见</span></strong><br/></p><p style=\"line-height: 29px; white-space: normal;\">　　<span style=\"font-family: 宋体;\">婴幼儿阶段是人的全面发展的开端。3岁以下幼儿以家庭养育为主，家长承担照看和养育孩子的主要责任。当前，人民群众对托育服务的需求日益强烈。为贯彻党的十九大精神，进一步落实《中共中央国务院关于实施全面两孩政策改革完善计划生育服务管理的决定》（中发〔2015〕40号），不断促进本市托育服务健康有序发展，现就促进本市3岁以下幼儿托育服务工作提出如下指导意见：</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>一、总体思路</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　托育服务是为3岁以下幼儿及其家长提供幼儿保育和科学育儿指导的服务。托育机构是指在本市行政区域内，由社会组织、企业、事业单位或个人举办，面向3岁以下幼儿，尤其是2-3岁幼儿实施保育为主、教养融合的幼儿照护的全日制、半日制或计时制机构。本市按照“政府引导、家庭为主、多方参与”的总体思路，建立健全托育服务工作管理的体制机制，促进托幼一体化发展，支持社会以多种形式提供托育服务，构建托育服务体系。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>二、主要目标</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><strong><span style=\"font-family: 宋体;\">　　（一）促进发展</span></strong></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　构建多形式、广参与的托育服务体系，积极推进托幼一体化工作，开展家庭科学育儿指导，引导本市托育服务市场发展，支持社会组织、企业、事业单位和个人积极提供符合适龄幼儿家庭多样化需求的托育服务。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（二）规范管理</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　建立和完善促进托育服务发展的政策法规和监督管理体系。制定托育机构（包括具有法人资格的托育机构和企事业单位、园区、商务楼宇举办的面向本单位职工适龄幼儿的免费福利性托育点，以下统称“托育机构”）设置标准和管理办法，明确托育机构法定代表人和托育点举办者是安全工作的第一责任人。落实管理主体和责任单位，健全管理组织架构和管理方式，形成长效监督管理机制，逐步规范本市托育服务市场。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（三）保障质量</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　明确场地和设施设备安全规范，落实人防、技防和物防等基本建设要求，保证必要的硬件质量。合理规划托育服务从业人员队伍建设，加强托育服务从业人员职业道德建设，明确岗位职责及资质要求，提高从业人员队伍素质，逐步提升托育服务质量。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>三、基本原则</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><strong><span style=\"font-family: 宋体;\">　　（一）规范引导</span></strong></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　各级政府要积极应对人民群众对托育服务的需求，加大政策扶持力度，循序渐进完善托育服务工作监督管理体系。通过制定设置标准和管理办法、健全监督管理体制与机制、开展学术研究、加强从业人员培养等，对托育服务市场进行引导和规范。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（二）多方参与</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　积极推动托幼一体化工作，充分调动社会力量，支持符合条件的社会组织、企业、事业单位和个人多方参与，营造共同承担社会责任的托育服务氛围，形成“规范化、多层次、多样化、可选择”的托育服务格局。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（三）加强监管</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　根据各级政府的管理职责和管辖范围，分级分层分类明确管理职责。建立托育服务的管理网络。明确本市托育机构的管理主体和相关部门职责分工，形成相关职能部门各司其职、协同推进的综合监管机制。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（四）优化服务</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　积极营造支持托育服务发展的社会环境，加大对托育机构扶持政策的宣传力度，做好申办程序的解释和指导工作，为托育机构举办者提供便利服务。充分发挥区托育服务管理机构、街镇指导站功能，为幼儿家庭就近就便提供科学育儿指导服务。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>四、主要任务</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><strong><span style=\"font-family: 宋体;\">　　（一）深入推进托幼一体化工作</span></strong></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　充分发挥本市托幼一体化的管理体制优势，加大托幼资源统筹力度，积极发挥幼儿园专业资源集聚优势。通过管理与辐射，提高资源使用效益，促进本市3岁以下幼儿托育服务质量的提升。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　鼓励有条件的区在新建配套幼儿园时，按照本市《普通幼儿园建设标准》（DG/TJ08-45）规定，落实托班的建设要求，满足举办托班的用房需求；通过改建、扩建幼儿园，增加托班的资源供给；鼓励民办幼儿园开设托班；支持公益性托儿所扩大服务对象的覆盖面，努力缓解本市入托的供需矛盾。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（二）支持社会力量依法举办托育机构</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　以多种方式引导支持社会组织、企业、事业单位和个人提供托育服务。引导支持举办者在居住社区、工作单位等场所单独或联合举办非营利性托育机构，为社区居民、单位职工提供更多公益性、福利性托育服务。在居住、就业集中建成区域，如符合消防安全、卫生防疫、环保等职能部门的相关要求，且房屋产权清晰、房屋性质不变更的，举办者可以试点结合住宅配套服务设施、商务办公、教育、科研、文化等建筑，综合设置幼儿托育设施。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　各类托育机构可根据本市2-3岁幼儿家庭的实际需求以及场地、供餐等条件，提供全日制、半日制或计时制等托育服务。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　1.引导企事业单位、园区和商务楼宇为职工提供托育服务。凡职工适龄子女达到20人及以上的企事业单位，鼓励单位积极挖掘现有资源或建设相应规模的托育机构，解决本单位职工子女托育问题。鼓励同一园区内、同一商务楼宇内的多家企事业单位，采用多种方式联合提供托育服务，满足园区内、商务楼宇内职工适龄子女的托育需求。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　2.鼓励挖掘社区资源满足托育服务需求。在各区政府的指导下，将托育服务纳入社区服务体系。挖掘社区空余场地资源，按照社区适龄人口数，合理规划和布局，提供托育服务的场所及亲子活动设施，具体可委托符合条件的托幼机构承接公益性托育服务。通过返聘教育、卫生计生等机构有经验的退休人员等措施，充实社区托育服务从业人员队伍，对社区内适龄幼儿家长等带养人提供科学育儿指导服务。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　3.引导和支持托育服务市场发展。引导和支持社会力量举办托育机构。通过提供专业培训、支持规模经营等方式促进托育服务市场发展。引导和培育相关食品企业根据幼儿不同的成长阶段，提供不同的预包装幼儿食品等配套服务。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（三）加强托育机构标准建设</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　按照专业要求和本市实际情况，制定统一的托育机构设置标准，并就选址、功能、供餐、安全、班级规模、人员配置等提出具体要求。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　1.选址要求。托育机构选址应以保障安全为先，远离污染源。托育机构建筑面积不低于360m<sup>2</sup>（只招收本单位、本社区适龄幼儿且人数不超过25人的，建筑面积不低于200m<sup>2</sup>），幼儿人均建筑面积不低于8m<sup>2</sup>。户外场地符合《托儿所、幼儿园建筑设计规范》（JGJ39）的机构，幼儿人均建筑面积不低于6m<sup>2</sup>。托育机构应尽可能为幼儿提供户外活动场地。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　2.功能要求。全日制、半日制托育机构的幼儿活动用房包括班级活动单元（含生活区与游戏活动区）、综合活动室等。计时制托育机构的幼儿活动用房包括生活区与游戏活动区。托育机构的服务用房包括保健观察室、晨检处、洗涤消毒用房等。全日制托育机构的附属用房包括厨房、储藏室等；半日制、计时制托育机构的附属用房包括配餐间、储藏室等。不提供点心的计时制托育机构可无配餐间。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　3.供餐要求。自行加工膳食的全日制托育机构应设不低于30m<sup>2</sup>的厨房，其加工场所和备餐间分别不低于23m<sup>2</sup>和7m<sup>2</sup>。不自行加工但提供膳食的全日制托育机构，需向有提供中小学餐饮服务资质的企业购买供餐服务，并设不低于8m<sup>2</sup>的配餐间。用餐人数超过50人的，执行本市食品经营许可中关于托幼机构食堂要求。半日制和计时制托育机构提供点心的、企事业单位自办托育点且其用餐由本单位食堂提供的，应设不低于8m<sup>2</sup>的配餐间。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　在保证食品安全的前提下，经食品药品监管部门认定，托育机构可采取多种形式的供餐服务，包括自有食堂、合法餐饮企业承包的食堂、所属单位内部食堂、周边具有供应能力和资质的食堂等。其中，单位内部食堂和周边食堂至托育机构的送餐时间应当控制在15分钟以内。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　4.安全要求。新建、改建、扩建托育机构，应符合《托儿所、幼儿园建筑设计规范》（JGJ39）和国家相关抗震、消防标准的规定。利用房龄20年以上的既有建筑提供托育服务的，须通过房屋结构安全检测。托育机构安全技术防范系统设计、检验、验收、维护等，应符合有关标准及智能安防系统要求。托育机构主出入口、幼儿生活及活动区域等，应安装视频安防监控系统，确保监控全覆盖，录像资料保存30天以上，且出入口设置微卡口，符合“智慧公安”相关要求。托育机构实施安全封闭管理，门卫室、安防控制中心、负责人办公室安装紧急报警装置，且与区域报警中心联网。托育机构根据实际场地，设置电子巡查系统，巡查点布控合理，安装须牢固隐蔽。此外，托育机构物防建设应符合国家和本市公安部门关于校园安全的规定。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　5.班级规模。托育机构按照幼儿年龄编班，2-3岁幼儿生活的班级，每班不超过20人；2岁以下幼儿生活的班级，每班不超过15人。全日制和半日制托育机构不宜超出6个班，计时制托育机构不宜超出4个班。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　6.人员配置。托育机构负责人应具有政治权利和完全民事行为能力，品行良好，身心健康，富有爱心，热爱保育工作，且须具有6年以上学前教育管理经历。托育机构应配备育婴员、保健员、保育员、营养员、财会人员、保安员等从业人员。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　托育机构收托幼儿数应与从业人员之间保持合理比例，每班应配备育婴员和保育员（统称“保育人员”），且育婴员不得少于1名。2-3岁幼儿与保育人员的比例应不高于7∶1，18-24个月幼儿与保育人员的比例应不高于5∶1，18个月以下幼儿与保育人员的比例应不高于3∶1。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　收托50人以下的托育机构，应至少配备1名兼职卫生保健人员；收托50-100人的，应至少配备1名专职卫生保健人员；收托101-140人的，应至少配备1名专职和1名兼职卫生保健人员。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　托育机构应至少有1名保安员在岗，保安员应由获得公安机关颁发的《保安服务许可证》的保安公司派驻。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（四）分类管理托育机构</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　举办非营利性托育机构（不含托育点）的，举办者需向所在地区业务主管单位递交《托育机构备案信息表》《托育机构卫生评价报告》、消防安全合格证明文件、举办者资格证明、场地证明、从业人员资格证明以及其他相关材料。经区业务主管单位审查同意后，举办者应向区民政部门提交相关文件，申请民办非企业单位法人登记，并按照实际条件和服务方式，申请登记为全日制、半日制或计时制托育机构。民政部门完成法人登记后，应及时将信息推送至区业务主管单位。非营利性托育机构完成法人登记后，应及时办理《食品经营许可证》。对符合运营要求的，由区托育服务管理机构向举办者发放《依法开展托育服务告知书》。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　企事业单位、园区和商务楼宇开办面向本单位职工适龄幼儿的免费福利性托育点，举办者情况、卫生评价情况、消防安全情况、场所情况等需在所属行政区托育服务管理机构备案，并在区相关职能部门指导下，由区托育服务管理机构牵头组织相关职能部门核查，达到《上海市3岁以下幼儿托育机构设置标准（试行）》（简称《设置标准》）及《上海市3岁以下幼儿托育机构管理暂行办法》（简称《管理办法》）相关要求的，方可正式运营。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　举办营利性托育机构（不含托育点），举办者应向所在地工商（市场监督管理）部门办理公司制法人登记，营业范围内注明全日制、半日制或计时制，专营托育服务；提交《托育服务告知承诺书》，承诺拟设托育机构符合《设置标准》和《管理办法》要求，在取得《托育机构卫生评价报告》《食品经营许可证》、消防安全合格证明文件、举办者资格证明、场所证明以及从业人员资格证明等材料之前，不得擅自从事经营活动。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　工商（市场监督管理）部门完成公司制法人登记后，通过信息化手段，将信息及时推送至区教育部门。区托育服务管理机构获得信息后，应在20个工作日内，组织协调相关职能部门开展并完成检查。对符合要求的，由区托育服务管理机构向举办者发放《依法开展托育服务告知书》。对不符合要求的，各相关职能部门应依法告知，责令限期整改；整改不达标却从事经营活动的，由相关职能部门依法查处，符合法律、行政法规规定吊销营业执照情形的，提请工商（市场监督管理）部门吊销营业执照。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（五）加强托育服务队伍建设</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　1.加强托育服务从业人员职业道德教育。培养关爱并尊重幼儿、具有科学育儿观、高度责任心和良好服务态度的高素质、专业化托育服务队伍。把托育服务从业人员的职业道德教育作为岗前入职、在岗及转岗培训中的必修课，主要内容应包括职业规范、职业责任、心理健康和安全意识等职业素养。职业道德教育原则上每年不少于40课时。加强托育服务从业人员的优秀事迹宣传，营造尊重托育服务从业人员的良好氛围。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　2.拓宽托育服务从业人员职前培养途径。扩大学前教育专业人才培养规模，建立贯通培养体系，引导部分职业院校、开放大学新增相关专业，鼓励学校通过中高贯通、中本贯通、高本贯通等方式，培养涉及育婴、保育、保健及托幼管理等相关专业人才。可由社会职业培训机构、开放大学系统等加强对托育从业人员的岗前培训。托育从业人员经培训取得相应资格后，按照规定持证上岗。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　3.加强对托育服务从业人员职后教育培训。依托本市多种资源定期开展职后培训，提升在职人员的托育服务水平。托育从业人员每年应完成不低于72课时的在职培训。充分利用终身教育资源、职业院校与开放实训中心，对育婴员、保育员、保健员、营养员等需持证上岗的从业人员开展职后培训。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　4.加强家庭科学育儿指导队伍的建设。依托开放大学及各区分校、各街镇社区学校，建立隔代养育指导专兼职师资队伍，开设各类科学育儿课程。挖掘社会现有资源，通过组织各类志愿者队伍，返聘托幼园所、儿保等领域有经验的退休人员等办法，建立开放、多元的隔代养育指导队伍。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　充分利用和挖掘各区社区院校等已有的师资力量，通过讲座、视频直播等途径，定期开展社区父母育儿指导师资队伍的业务培训和交流活动，培育开放、专业的父母育儿指导队伍。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　5.加强托育服务从业人员管理。建立相应的准入标准，原则上托育服务从业人员应具有中职以上学历，并具有保育员或育婴员等国家职业资格证书。建立健全托育机构及从业人员的评估监测制度，加强事中事后监管，对于违反职业道德的从业人员，建立行业从业人员“黑名单”制度。鼓励相关社会团体发布托育机构从业人员市场工资指导价，引导业内合理确立托育机构从业工资待遇，确保托育机构从业人员依法纳入社保体系。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（六）加强对家庭科学育儿的指导</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　1.明确任务要求。重视和加强家庭科学育儿指导，倡导以家庭为主的育儿模式，对家庭育儿给予专业支持，在全社会形成重视家庭科学育儿的氛围和环境。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　2.健全管理网络。建立由教育、卫生计生、妇联、工会等多部门合作的覆盖全市各街镇的科学育儿指导服务网络，设置市、区两级托育服务管理机构与街镇工作站点，配齐相关专业指导人员。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　3.拓宽实施途径。整合各方资源，聚焦广大家长的育儿困惑，积极通过公益活动、各种媒体、信息技术提供专业宣讲与咨询、资讯等多种服务，缓解广大家长的育儿焦虑。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　4.注重科学育儿研究与指导。开展对科学育儿指导工作的研究，编制指导课程和方案。每年为本市适龄幼儿家庭提供至少6次免费科学育儿指导服务，引导家长树立科学的育儿观，掌握科学育儿方法和技能。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　5.加强评估与质量监测。研制并实施科学育儿指导工作规范与评估办法，加强对各区育儿指导工作的质量监督，保障适龄幼儿家庭受益。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>五、管理体制</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　（一）坚持分级管理</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　市政府制定托育服务规划，组织协调各职能部门制定相关标准、管理办法等，共同推进托育服务工作。建立市托幼工作联席会议，定期商议解决本市托育服务管理工作中的重大问题。托幼工作联席会议下设办公室，办公室设在市教委。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　区政府是落实托育服务工作的责任主体，负责制定区域内的托育服务工作规划，加强管理队伍建设和用地保障，指导街镇将公益性托育服务纳入社区公共服务体系，引导托育机构合理布局和规范发展。建立区托幼工作联席会议，定期商议解决本区托育服务管理工作中的重大问题。托幼工作联席会议下设办公室，办公室设在区教育局。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　街道办事处、乡镇政府负责具体组织实施综合监管。街镇要协调区域内社区卫生服务中心、派出所、育儿服务指导站、市场监管所等部门及时掌握区域内托育服务的发展情况，包括托育机构数量、服务质量、定价收费以及服务人群等。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　市教委牵头管理托育服务工作，会同各相关职能部门执行市政府和市托幼工作联席会议的相关决策，制定工作实施计划。完善相关机构设置，指导各区开展托育服务工作管理。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　区教育局牵头负责区域内托育服务工作的管理，规划区域托育服务的发展，制定区域内托育服务管理办法。根据区域内适龄幼儿的人数规模，建立托幼工作管理机构，充实管理力量，加强对托育机构的监督管理。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　市托育服务管理机构负责指导和管理各区托育服务管理机构开展托育服务工作，调查研究托育服务工作情况，定期报送本市托育机构发展情况；多途径整合专业资源，组织并指导各区开展面向3岁以下婴幼儿家庭科学育儿指导的公益性活动；开展本市托育服务管理人员、业务骨干（含外籍从业人员）等队伍的培训和国际合作交流；组建专业巡查队伍，保障本市托育服务工作质量。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　区托育服务管理机构作为公益性事业单位，在区教育局的领导下，负责区域内托育机构预约登记、申办咨询、备案等工作，组织协调对区域内托育机构的事中事后监管和对区域内婴幼儿家庭科学育儿指导工作，负责区域内托育机构从业人员培训工作；组建专业巡查队伍，对区域内托育机构开展定期与不定期的评估检查。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（二）坚持部门联动</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　教育、民政、工商、卫生计生、食品药品监管、公安、消防、人力资源社会保障、住房城乡建设、房屋管理、规划国土资源、财政、物价、地税、工会、妇联等部门和群团组织要密切配合，加强协作，形成政府统筹领导，教育部门牵头，相关部门分工合作和乡镇（街道）、托育机构、单位、家庭共同参与的格局，共同做好本市托育服务工作。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　教育部门牵头研制托育机构设置标准和管理办法等，研制托育服务发展规划，会同各相关职能部门对托育机构进行监督管理和业务指导。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　民政部门负责本市行政区域内民办非营利性托育机构（不含托育点）的登记管理工作，对低保低收入困难家庭中的幼儿及时提供社会救助。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　工商（市场监督管理）部门负责本市行政区域内营利性托育机构（不含托育点）的登记管理工作，依法查处违反相关法律法规的行为。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　卫生计生部门负责对本市托育机构卫生保健、疾病防控等工作进行技术指导与支持，依法对托育机构的饮用水卫生、传染病防控等进行监督检查。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　食品药品监管部门负责对托育机构的食品安全提出要求，指导区市场监管部门开展食品安全监管。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　公安部门参与托育机构的安全管理标准制定，负责对托育机构的安全管理工作进行监管。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　消防部门参与托育机构的消防安全标准制定，负责对托育机构中的消防安全工作依法进行监督检查。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　人力资源社会保障部门负责确立托育服务从业人员职业发展和类型，指导各区开展职业技能培训等。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　住房城乡建设管理部门负责托育机构工程技术标准的立项、编号、备案、批准发布工作。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　房管部门负责指导各区依规划要求提供托育服务的配套房屋设施。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　规划国土资源部门负责指导各区开展托育机构规划和土地管理相关工作。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　财政部门负责对本市相关行政部门开展托育管理等工作提供经费保障。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　物价部门负责规范托育服务收费行为，做好与幼儿园收费政策衔接，加强对托育机构的定价指导和收费行为的监督管理。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　地税部门负责托育服务相关税政业务和征收管理工作，支持托育机构享受国家相关税收优惠政策。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　工会负责调查研究职工托育需求，对企事业单位内举办的托育点进行监督和指导，推动托育服务从业人员职业道德建设。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　妇联协同推进社区托育机构的设点布局，参与为家庭提供科学育儿指导服务，加强对女性从业人员的职业道德宣传和维权服务。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（三）坚持综合监管</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　在市委、市政府的领导下，建立市、区、街镇三级联动的综合监管机制，畅通投诉举报渠道，加大对托育服务市场违法违规行为的查处力度。在区政府相关职能部门和街镇的统一组织下，形成日常检查发现、归口受理和分派、违法查处等各环节分工牵头负责、共同履职的机制。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　建立日常检查机制。建立举办者自查、区托育服务管理机构网上巡查、区相关职能部门抽查和街镇牵头联合检查相结合的综合监管体系，开展托育服务市场违法违规行为的日常检查发现工作。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　建立归口受理和分派机制。建立由区托育服务管理机构牵头的归口受理机制，对巡查发现或投诉举报的线索进行初步核实，并分派到街镇或者相关职能部门处理。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　建立违法查处机制。托育机构涉及违反多个法律法规的，由各街镇牵头，整合区域内执法力量，会同区市场监督管理、消防、公安、卫生计生、教育、人力资源社会保障、民政等部门依据法定职责开展联合执法。涉嫌犯罪的，移交司法部门处理。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　建立诚信评价机制。建立托育机构诚信档案，形成违法失信惩戒制度。将托育机构的违法信息纳入本市公共信用信息服务平台。对托育机构是否符合规定的情况予以信息公告。依法公开托育机构的备案、登记等基本信息，监督检查结果和行政处罚信息等。加强事中事后监管，提高监测预警能力。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　建立行业自律机制。拓展相关社会团体功能，发挥社会组织行业自律、协调、规范等作用。组织全市托育服务从业人员学习政策法规，增强托育服务从业人员依法从业意识，促进行业自律和自觉。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　建立托育服务信息管理平台。充分利用互联网、大数据和智能终端设备，对托育机构的申办过程、综合监管、信息公开、诚信记录、人员信息以及业务数据等进行信息化管理。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>六、保障措施</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><strong><span style=\"font-family: 宋体;\">　　（一）加大人财物的保障力度</span></strong></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　1.加强管理队伍建设。市、区教育部门根据托育事业规模，增加专人负责托育服务工作，建立托育服务工作的管理队伍。明确区托育服务管理机构的工作职能，保障相应人员编制，配齐配足管理和工作人员。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　2.加大财政投入力度。财政部门将托育服务工作管理所需的经费等纳入各级政府财政预算，支持托育服务工作的发展，保障日常办公、监管与指导、专业研究和人才队伍培养等工作。对社会力量举办具有公益性的非营利性托育机构给予支持。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　3.加强硬件支持与保障。保障托育服务工作管理所需的办公场所、设施设备以及其他必要的硬件装备，建立网上巡查平台，做好信息数据对接和推送工作，支持托育服务管理，指导托育服务发展。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（二）加大用地和房屋保障力度</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　将托育机构建设用地纳入城乡规划和年度用地计划并优先予以保障，农用地转用指标、新增用地指标分配要适当向托育机构建设用地倾斜。将托育服务设施纳入本市控详规划的内容。引导支持利用低效土地或闲置土地建设托育机构。对非营利性托育机构建设用地，经行业主管部门认定，符合《划拨用地目录》要求的，可采取划拨方式予以保障。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　<strong>（三）加大政策支持力度</strong></span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　1.托育机构从业人员可接受定期的免费专业培训。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　2.托育机构用水、用电、用气实行居民价格。托育机构如需实行居民水电气价格，由机构向水电气企业提出申请，并提交市或区托育服务管理机构出具的证明材料，由水电气企业核实后办理。如需安装独立计量装置、涉及线路改造的，有关费用由托育机构承担。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　3.托育机构可按照规定，申请设立青年（大学生）职业训练营或就业见习基地，培养专业服务队伍，并按照规定，获得政策扶持。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　4.将保育员、育婴员等相关技能培训项目列入本市职业技能培训补贴目录。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　5.托育服务从业人员参加保育员、育婴员等项目培训并鉴定合格的，可按照紧缺培训补贴项目规定标准，给予培训费补贴。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　6.符合条件的托育机构提供托育服务，予以免征增值税。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　7.符合条件的托育机构可向财税部门申请办理非营利组织免税资格认定，经认定后可按照规定，享受非营利组织有关收入免征企业所得税政策。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　8.企业设立的员工子女托育点所发生的费用，可按照税法规定，作为职工福利费支出在税前扣除。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　9.购买综合保险、开展食品安全等工作取得成效的托育机构，可给予综合奖补。</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 宋体;\">　　10.鼓励中心城区范围内的商务楼宇综合设置公益性托育机构。已出让商办用地新增公益性托育机构的，可<strong>享受</strong>城市更新政策。</span></p><p><span style=\"font-family: 宋体;\"><br/></span></p><p><br/></p>',51,'N','2018-08-22 09:26:58',0,3,-1,-1,-1,'2018-08-22 09:26:58',-1,'2018-09-30 11:30:03',-1,'ID',NULL,NULL,NULL);
INSERT INTO `kg_news` (`id`,`typeId`,`sourceId`,`attributeId`,`newsTitle`,`summary`,`thumbnail`,`author`,`content`,`sequence`,`indexshow`,`createDate`,`viewsCount`,`OBJECT_VERSION_NUMBER`,`REQUEST_ID`,`PROGRAM_ID`,`CREATED_BY`,`CREATION_DATE`,`LAST_UPDATED_BY`,`LAST_UPDATE_DATE`,`LAST_UPDATE_LOGIN`,`CERTIFICATE_TYPE`,`EFFECTIVE_START_DATE`,`EFFECTIVE_END_DATE`,`ATTRIBUTE_CATEGORY`) VALUES 
 (1065,17,1,'','上海市人民政府办公厅关于印发《上海市3岁以下幼儿托育机构管理暂行办法》的通知','上海市3岁以下幼儿托育机构管理暂行办法','',NULL,'<p style=\"text-align: center; white-space: normal;\"><span style=\"font-family: 华文中宋; font-size: 24px;\">上海市人民政府办公厅关于印发《上海市3岁以下幼儿托育机构管理暂行办法》的通知</span></p><p style=\"text-align: center; margin-top: 10px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">沪府办规〔2018〕12号</span></p><p style=\"margin-top: 10px; margin-bottom: 10px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">各区人民政府，市政府各委、办、局，各有关单位：</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　《上海市3岁以下幼儿托育机构管理暂行办法》已经市政府同意，现印发给你们，请认真按照执行。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">&nbsp;</span></p><p style=\"margin: 0px 21px 0px 312px; text-align: right; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">上海市人民政府办公厅</span></p><p style=\"margin: 0px 21px 0px 312px; text-align: right; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">2018</span><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">年4月28日</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">&nbsp;</span></p><hr style=\"white-space: normal;\"/><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\"></span><br/></p><p style=\"text-align: center; white-space: normal;\"><span style=\"font-family: 华文中宋; font-size: 24px;\">上海市3岁以下幼儿托育机构管理暂行办法</span></p><p style=\"text-align: center; margin-top: 21px; white-space: normal;\"><strong><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">第一章　总则</span></strong></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第一条（目的依据）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　为规范和加强本市托育机构（包括具有法人资格的托育机构和企事业单位、园区、商务楼宇举办的面向单位职工适龄幼儿的免费福利性托育点，以下统称“托育机构”）管理，促进3岁以下幼儿托育服务事业健康有序发展，根据有关法律法规和相关政策要求，制定本暂行办法。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第二条（适用范围）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　本市行政区域内3岁以下托育机构的设立、运营、管理、监督等，适用本暂行办法。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　本暂行办法所指的托育机构，是指在本市行政区域内，由社会组织、企业、事业单位或个人举办，面向3岁以下尤其是2-3岁幼儿实施保育为主、教养融合的幼儿照护的全日制、半日制、计时制机构。幼儿园托班和托儿所不适用本暂行办法。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第三条（服务宗旨）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构应当坚持托育服务的公益性，坚持“以幼儿发展为本”的原则，遵守法律法规，遵循幼儿的生理和心理发展规律与特点，促进幼儿健康成长。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第四条（管理分工）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　市政府制定托育服务规划，组织协调各职能部门制定相关标准、管理办法等，共同推进托育服务工作。建立市托幼工作联席会议，定期商议解决本市托育服务管理工作中的重大问题。市托幼工作联席会议下设办公室，设在市教委。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　区政府是落实托育服务工作的责任主体，负责制定区域内托育服务工作规划，加强管理队伍建设和用地保障，指导街镇将公益性托育服务纳入社区公共服务体系建设，引导托育机构合理布局和规范发展。建立区托幼工作联席会议，定期商议解决本区托育服务管理工作中的重大问题。区托幼工作联席会议下设办公室，设在区教育局。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　街道办事处、乡镇政府负责具体组织实施托育服务工作的综合监管。街镇协调区域内社区卫生服务中心、派出所、育儿服务指导站、市场监管所等部门及时掌握区域内托育服务的发展情况，包括托育机构数量、服务质量、定价收费以及服务人群等。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　教育部门牵头管理本市托育服务工作，牵头研制托育机构设置标准和管理办法等，制定托育服务发展规划和工作实施计划，会同各相关职能部门执行托幼工作联席会议的相关决策，对托育机构进行监督管理和业务指导。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　民政部门负责本市行政区域内民办非营利性托育机构（不含托育点）的登记管理工作，对低保低收入困难家庭中的幼儿及时提供社会救助。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　工商（市场监督管理）部门负责本市行政区域内营利性托育机构（不含托育点）的登记管理工作，依法查处违反相关法律法规的行为。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　卫生计生部门负责对本市行政区域内托育机构卫生保健、疾病防控等工作进行技术指导与支持，依法对托育机构的饮用水卫生、传染病防控等进行监督检查。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　食品药品监管部门负责对托育机构的食品安全提出要求，指导区市场监督管理部门开展食品安全监管。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　公安部门参与托育机构的安全管理标准制定，负责对本市行政区域内托育机构的安全管理工作进行监管。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　消防部门参与托育机构的消防安全标准制定，负责对本市行政区域内托育机构中的消防安全工作依法进行监督检查。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　人力资源社会保障部门负责确立托育服务从业人员职业发展和类型，指导各区开展职业技能培训等。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　住房城乡建设管理部门负责托育机构工程技术标准的立项、编号、备案、批准发布工作。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　房管部门负责指导各区依规划要求提供托育服务的配套房屋设施。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　规划国土资源部门负责指导各区开展托育机构规划和土地管理相关工作。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　财政部门负责对本市相关行政部门开展托育管理等工作提供经费保障。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　物价部门负责规范托育服务收费行为，促进与幼儿园收费政策衔接，加强对托育机构定价指导和收费行为的监督管理。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　地税部门负责托育服务相关税政业务和征收管理工作，支持托育机构享受国家相关税收优惠政策。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　工会负责调查研究职工托育需求，对企事业单位内举办的托育点进行监督和指导，推动托育服务从业人员职业道德建设。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　妇联协同推进社区托育机构的设点布局，参与为家庭提供科学育儿指导服务，加强对女性从业人员的职业道德宣传和维权服务。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　市托育服务管理机构负责指导和管理各区托育服务管理机构开展托育服务工作，调查研究托育工作情况，定期报送本市托育机构发展情况。多途径整合专业资源，组织并指导各区开展面向3岁以下婴幼儿家庭科学育儿指导的公益性活动。开展本市托育服务管理人员、业务骨干（含外籍从业人员）等队伍的培训和国际合作交流。组建专业巡查队伍，保障本市托育服务工作质量。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　区托育服务管理机构作为公益性事业单位，在区教育局的领导下，负责区域内托育机构预约登记、申办咨询、备案等工作，组织协调对区域内托育机构的事中事后监管和对区域内婴幼儿家庭科学育儿指导工作，负责区域内托育机构从业人员培训工作。组建专业巡查队伍，对区域内托育机构开展定期与不定期的评估检查。</span></p><p style=\"text-align: center; white-space: normal;\"><strong><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">第二章　机构设立</span></strong></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第五条（机构类型）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构（不含托育点）分为营利性和非营利性两类。营利性托育机构（不含托育点）利用非国家财政性经费和非捐助资产设立，在工商（市场监督管理）部门办理公司制法人登记；非营利性托育机构（不含托育点）不以营利为目的，利用非国家财政性经费捐助申请设立，经业务主管单位同意后，在民政部门依法办理民办非企业单位法人登记。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第六条（举办资格）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　申请举办托育机构的社会组织、企业、事业单位应当具有独立法人资格。事业单位出资举办的，应当经其上级主管单位批准同意；国有及国有控股企业投资举办的，应当向对其国有资产负有监管职责的机构履行备案手续。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　申请举办托育机构的个人应当具有中华人民共和国国籍，并具有政治权利和完全民事行为能力。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第七条（选址要求）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构功能布局、建筑设计、设施设备等应当以保障安全为先，符合下列要求：</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（一）地质条件较好、环境适宜、空气流通、日照保障、交通方便、场地平整干燥、排水通畅、基础设施完善、周边环境适宜、邻近绿化带、符合卫生和环保要求的宜建地带。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（二）避开可能发生地质灾害和洪水灾害等不安全地带，避开加油站、输油输气管道和高压供电走廊等。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（三）不与公共娱乐场所、集市、批发市场等人流密集、环境喧闹、杂乱或不利于幼儿身心健康成长的建筑物及场所毗邻，远离医院、垃圾及污水处理站等危及幼儿安全的各种存在污染源的场所，远离城市交通主干道或高速公路等，符合国家现行有关卫生、防护标准的要求。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（四）3个班及以上的全日制、半日制托育机构以及企事业单位、园区或商务楼宇开办的托育机构，宜独立设置。在居住、就业集中区域，如符合消防安全、卫生防疫、环保等专业管理部门的相关要求，且房屋产权清晰，房屋性质不变更的，举办者可试点结合住宅配套设施、商务办公、教育、科研、文化等建筑综合设置幼儿托育设施，但须符合下列规定：</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　1.新建、改建、扩建托育机构（点）应当符合《托儿所、幼儿园建筑设计规范》（JGJ39）和国家相关抗震、消防标准的规定。利用房龄20年以上的既有建筑提供托育服务的，须通过房屋结构安全检测。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　2.幼儿生活用房不设置在地下或半地下，当设置在建筑的首层确有困难时，可设置在地上二、三层，除应当符合《建筑设计防火规范》要求外，还应当满足以下条件：</span></p><p style=\"text-indent: 43px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">（1）设置在一、二级耐火等级的建筑内。</span></p><p style=\"text-indent: 43px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">（2）场所下方对应区域也为托育机构用房。</span></p><p style=\"text-indent: 43px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">（3）设置独立的安全出口和疏散楼梯。</span></p><p style=\"text-indent: 43px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">（4）场所采用耐火极限不低于2.00h的防火隔墙和1.00h的楼板与其他场所或部位分隔，墙上必须设置的门、窗应采用乙级防火门、窗。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（5）场所设置自动喷水灭火系统和火灾自动报警设施。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（6）场所顶棚的内装修材料为A级，墙面、地面、隔断、装饰织物和其他装饰材料不低于B1级。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　3.托育机构出入口处设置人员安全集散和车辆停靠的空间，且不影响城市道路交通。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　4.宜设独立的室外活动场地，场地周围采取安全隔离措施，防止走失、失足、物体坠落等风险。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第八条（举办场所）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　举办者应当提供能满足使用功能要求、与举办项目和举办规模相适应的场所，提供区级及以上卫生计生部门指定的医疗卫生机构出具的符合《托儿所幼儿园卫生保健工作规范》的卫生评价报告，以及消防等相关职能部门出具的用房安全合格或许可证明。举办者租用场地的，租赁期限自申请开办托育机构之日起不得少于3年。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第九条（建筑设计）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构的建筑布局、结构、防火、各室及区域的设计、层高、走廊、楼地面、内外墙、门窗等，均应当符合《托儿所、幼儿园建筑设计规范》（JGJ39）中的相关规定。托育机构的消防设计应当符合《建筑设计防火规范》（GB50016）等国家标准。技防、物防建设应当符合国家和市公安部门有关校园安全的规定。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第十条（建筑面积）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构建筑面积不低于360m<sup>2</sup>（只招收本单位、本社区适龄幼儿且人数不超过25人的，建筑面积不低于200m<sup>2</sup>），且幼儿人均建筑面积不低于8m<sup>2</sup>；户外场地符合《托儿所、幼儿园建筑设计规范》（JGJ39）的，幼儿人均建筑面积不低于6m<sup>2</sup>。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第十一条（功能要求）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（一）幼儿游戏活动室（区）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　应当配备适宜幼儿的桌椅和玩具柜。幼儿桌椅表面及幼儿手指可触及的隐蔽处，均不得有锐利的棱角、毛刺及小五金部件的锐利尖端。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　应当配备数量充足、种类多样的玩具和图书，以及可供幼儿摆弄和操作的各种材料。提供给幼儿的玩具应当符合GB6675“《玩具安全》系列国家标准”。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（二）幼儿就寝室（区）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　不得设双（多）层床，床位侧面不得紧靠外墙布置。班级活动单元内不得搭建阁楼或夹层作寝室。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（三）厨房</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　厨房平面布置应当符合食品安全规定，满足使用功能要求。厨房不得设在幼儿活动用房的下部。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　自行加工膳食的全日制托育机构应当设置不低于30m<sup>2</sup>的厨房，其中加工场所（包括初加工、切配、烹饪等）和备餐间分别不低于23m<sup>2</sup>和7m<sup>2</sup>。不自行加工但提供膳食的全日制托育机构，需向有提供中小学餐饮服务资质的企业购买供餐服务，并设不低于8m<sup>2</sup>的配餐间。用餐人数超过50人的，执行本市食品经营许可中有关托幼机构食堂要求。半日制和计时制托育机构提供点心的，企事业单位、园区或商务楼宇自办托育点且其用餐由本单位、园区或商务楼宇食堂提供的，应当设不低于8m<sup>2</sup>的配餐间。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　在保证食品安全的前提下，经食品药品监管部门认定，托育机构可采取多种形式的供餐服务，包括自有食堂、合法餐饮企业承包的食堂、所属单位内部食堂、周边具有供应能力和资质的食堂等。其中，单位内部食堂和周边食堂至托育机构的送餐时间应当控制在15分钟以内。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（四）建筑设备</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　按照信息化管理要求，敷设网络、通信、有线电视、安保监控等线路，预留接口。办公室内应当设有监控视频观察区，对托育机构内所有场所（成人洗手间及更衣间除外）进行无死角监控。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（五）附属设施</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　主出入口、幼儿生活及活动区域等应当安装视频安防监控系统，确保监控全覆盖，录像资料保存30日以上，且出入口设置微卡口，符合“智慧公安”相关要求。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　设置安全、通透的实体周界，实施全封闭管理，周界宜设置入侵报警系统。根据实际场地，设置电子巡查系统，巡查点布控合理，安装须牢固隐蔽。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构门卫室、安防控制中心、负责人办公室应当安装紧急报警装置，且与区域报警中心联网。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　根据消防要求，在托育机构区域内和建筑内，配置相应的消防设备。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第十二条（名称规范）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　申请设立非营利性托育机构（不含托育点）的，其名称应当符合《民办非企业单位登记管理暂行条例》《民办非企业单位名称管理暂行规定》等法规规章的规定。非营利性托育机构（不含托育点）的名称，依次由市区行政区划名称、字号（两个以上的汉字组成）、托育、组织形式四部分组成。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　申请设立营利性托育机构（不含托育点）的，其名称应当符合《公司登记管理条例》《企业名称登记管理规定》《上海市企业名称登记管理规定》等法规规章的规定。营利性托育机构（不含托育点）的名称，依次由市区行政区划、字号（两个以上的汉字组成）、托育、组织形式四部分组成。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构（不含托育点）只能使用一个名称，其外文名称应当与中文名称语义一致。名称应当符合中国国情，不得冠以“中国”“全国”“中华”“世界”“国际”“全球”等字样。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第十三条（设立申请）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　申请设立3岁以下托育机构的，应当符合《上海市3岁以下幼儿托育机构设置标准(试行)》（简称《设置标准》）的要求，取得下列材料并通过检查：</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（一）由区级及以上卫生计生部门指定的医疗卫生机构出具的《托育机构卫生评价报告》。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（二）由区公安消防部门出具的消防安全合格证明文件。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（三）由区市场监督管理局出具的《食品经营许可证》。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（四）举办者资格证明。举办者为社会组织、企事业单位的，应当提供法人资格证明材料；举办者为个人的，应当提供身份证明、户籍证明等。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（五）场地证明。自有场所的，应当提供房屋产权证明；租赁场所的，应当提供出租方的房屋产权证明以及具有法律效力的租赁协议。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（六）从业人员资格证明。包括身份证明、学历证明、健康证明、从业资格证明等。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　申请人应当如实提供有关材料情况，并对所提交材料的真实性负责。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第十四条（受理与登记）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　举办非营利性托育机构（不含托育点）的，举办者需向所在区业务主管单位递交《托育机构备案信息表》《托育机构卫生评价报告》、消防安全合格证明文件、举办者资格证明、场地证明、从业人员资格证明以及其他相关材料。经区业务主管单位审查同意后，举办者应当向区民政部门提交相关文件，申请办理民办非企业单位法人登记，并按照实际条件和服务方式，申请登记为全日制、半日制或计时制托育机构。民政部门完成法人登记后，应当及时将信息反馈区业务主管单位。非营利性托育机构（不含托育点）完成法人登记后，应当及时办理《食品经营许可证》。对符合运营要求的，由区托育服务管理机构向举办者发放《依法开展托育服务告知书》。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　企事业单位、园区和商务楼宇开办面向单位职工适龄幼儿的免费福利性托育点的，举办者情况、卫生评价情况、消防安全情况、场所情况等需在所在区托育服务管理机构备案，并在区相关职能部门指导下，由所在区托育服务管理机构牵头组织相关职能部门进行核查，达到《设置标准》及本办法要求的，方可正式运营。托育点的活动和管理，由企事业单位、园区和商务楼宇管委会实施并承担主体责任。两家以上单位联合举办面向单位职工适龄幼儿托育点的，应当成立管理委员会，负责托育点的活动和管理，并由联合举办单位承担相应责任。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　举办营利性托育机构（不含托育点）的，举办者应当向工商（市场监督管理）部门办理公司制法人登记，经营范围内注明全日制、半日制或计时制，专营托育服务。提交《托育服务告知承诺书》，承诺拟设托育机构符合《设置标准》和本暂行办法要求，在取得《托育机构卫生评价报告》《食品经营许可证》、消防安全合格证明文件、举办者资格证明、场所证明以及从业人员资格证明等材料之前，不得擅自从事经营活动。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　工商（市场监督管理）部门完成公司制法人登记后，应当通过信息化手段将信息及时推送至教育部门。区托育服务管理机构获得信息后，应当在20个工作日内，组织协调相关职能部门开展并完成检查。对符合要求的，由区托育服务管理机构向举办者发放《依法开展托育服务告知书》。对不符合要求的，各相关职能部门应当依法告知，责令限期整改；整改不达标仍从事经营活动的，由相关职能部门依法查处，符合法律、行政法规规定吊销营业执照情形的，提请工商（市场监督管理）部门吊销营业执照。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第十五条（信息公示）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　各区托育服务管理机构应当及时将区域内登记备案的托育机构信息在其官方网站公开，便于社会查询、监督。</span></p><p style=\"text-align: center; white-space: normal;\"><strong><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">第三章　机构管理</span></strong></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第十六条（组织机构）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构应当坚持和加强党的领导，做到党的建设同步谋划、党的组织同步设置、党的工作同步开展。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构应当按照相关法律法规规定，建立决策机构和执行机构。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构应当按照《设置标准》中有关任职条件，聘任托育机构负责人，由其依法独立负责托育机构的照护服务和行政管理工作。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构依法通过职工大会等形式，保障从业人员参与民主管理和监督。托育机构的从业人员有权依照工会法，建立工会组织，维护其合法权益。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第十七条（证件管理）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构（不含托育点）应当在公开场所的显著位置，公示法人登记证明以及依法开展经营的证明文件。托育点应当在公开场所的显著位置公示依法开展托育服务的证明文件。证明文件遗失的，应当立即公告，并及时向颁发机关申请补办。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构不得以任何名义出租、出借、转让上述证明文件。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构（不含托育点）的《民办非企业单位（法人）登记证》或《营业执照》期满需要延续的，应当在有效期满30日前，向登记管理机关提出延续申请，并提交相应证明材料。登记管理机关应当作出是否同意的决定。同意延续的，应当收回原件后换发登记证或营业执照；不同意延续的，应当书面说明理由。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第十八条（保育管理）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（一）基本要求</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构应当贯彻以保育为主、教养融合的原则，遵循幼儿生长发育和心理发展规律，创设适宜的养育环境，关爱幼儿，以幼儿发展为本，把幼儿的安全、健康和照护工作放在首位，注重幼儿发展的整体性，促进幼儿身心健康发展。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（二）主要内容</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　1.创设清洁卫生、安全温馨、便于活动的生活环境，提供数量充足的、安全的、能满足多种感知需要的玩具材料。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　2.设置的活动应当以照护为主，应当有利于幼儿身心健康发展。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　3.保育人员应当熟知幼儿的发展特点，能观察了解幼儿的需要和情绪变化，给予关爱。保育人员应当多与幼儿进行面对面、一对一的个别交流，有针对性地开展照护工作。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　4.尊重、顺应幼儿生理节律，加强生活护理，培养幼儿的自理能力。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　5.以游戏为主要的活动形式，鼓励幼儿操作、摆弄、探索和交往，丰富幼儿的直接经验。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　6.将保护幼儿生命安全放在首位，及时处理幼儿的常见事故，危险情况优先救护幼儿。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（三）日常组织</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构应当合理安排幼儿生活，做好幼儿生活安排，悉心照料幼儿入托、喂奶、饮水、进餐、换尿布、如厕、盥洗、穿脱衣服、睡眠、室内游戏和室外活动等每一个环节，各环节时间安排要相对固定，活动方式要灵活多样，以个别、小组活动形式为主，集中统一活动时间不宜过长。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构不得开展违背幼儿养育基本要求、有损幼儿身心健康的活动。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　严禁虐待、歧视、体罚或变相体罚等损害幼儿身心健康的行为。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（四）沟通合作</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构应当主动与幼儿家庭沟通合作，建立托育机构与幼儿父母联系的制度，指导幼儿父母正确了解托育机构养育的内容方法，了解幼儿家庭在养育幼儿过程中遇到的问题和困难，指导帮助幼儿父母提高科学育儿能力。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　鼓励托育机构开展幼儿与家长的亲子互动活动。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（五）组织管理</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构应当搞好养育的组织与管理，制定养育方案，创设安全、卫生、温馨的环境，开展养育活动评价，建立业务档案、信息管理等制度。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第十九条（人员管理）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（一）人员配置</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构负责人应当具有政治权利和完全民事行为能力，品行良好，身心健康，富有爱心，热爱保育工作。托育机构应当按照《设置标准》配备育婴员、保健员、保育员、营养员、财会人员、保安员等从业人员。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构收托幼儿数应当与从业人员之间保持合理比例，每班应当配育婴员和保育员，且育婴员不得少于1名。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　2-3岁幼儿与保育人员（育婴员和保育员）的比例不得高于7∶1，18-24个月幼儿与保育人员（育婴员和保育员）的比例不得高于5∶1，18个月以下幼儿与保育人员（育婴员和保育员）比例不得高于3∶1。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　收托50人以下的托育机构（点），应当至少配备1名兼职卫生保健人员；收托50-100人的，应当至少配备1名专职卫生保健人员；收托101-140人的，应当至少配备1名专职和1名兼职卫生保健人员。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　每个托育机构应当至少有1名保安员在岗,该保安员应由获得公安机关颁发的《保安服务许可证》的保安公司派驻。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　有下列情况的人员，不得在托育机构工作：</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　1.有刑事犯罪记录的。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　2.有吸毒记录和精神病史的。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　3.未取得健康证明的。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　4.其他不适宜从事托育服务的。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构从业人员患传染病期间，暂停其在托育机构的工作。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（二）人员资质</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构专职负责人应当具有大专及以上学历，同时具有教师资格证和育婴员四级及以上国家职业资格证书，有从事学前教育管理工作6年及以上的经历，能胜任机构管理工作。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　育婴员应当具有大专及以上学历，并取得育婴员四级及以上国家职业资格证书。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　保健员应当具有中等卫生学校、幼师或高中以上文化程度，经过本市妇幼保健机构组织的卫生保健专业知识培训并考核合格。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　保育员应当取得保育员四级及以上国家职业资格证书。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　保健员、营养员等托育机构食品安全管理人员、关键环节操作人员应当取得食品安全知识培训考核合格证书。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　保安员须由获得公安机关颁发的《保安服务许可证》的保安公司派驻，并均应当经公安机关培训取得《保安员证》。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（三）人员管理</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构应当遵照《中华人民共和国劳动合同法》与被聘用的从业人员签订聘任合同或劳动合同，依法保障在职人员的合法权益。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构应当建立人员培训、职级评定等制度，建立负责人和育婴员继续教育制度，并按规定组织和支持从业人员参加继续教育进修活动，不断提高托育服务队伍的素质。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第二十条（安全管理）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（一）责任主体</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构（不含托育点）法定代表人和托育点举办者，是机构安全和卫生保健工作的第一责任人。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（二）安全制度</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构实施全封闭管理，报警系统确保24小时设防。安防监控系统录像资料应当保存30日以上。托育机构应当建立健全（门卫、房屋、设备、消防、交通、食品、药物、幼儿接送交接、活动组织和幼儿就寝值守等）安全防护制度。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构应当建立安全责任制，加强对举办场所、活动场地和设施设备的安全检查，落实各项安全防范措施，执行日巡查制度，做好安全巡查记录，及时消除安全隐患。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（三）突发事件应对</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构应当建立重大自然灾害、食物中毒、踩踏、火灾、暴力等突发事件的应急预案，规定突发事件发生时优先保护幼儿的相应措施。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构内应当配备接受过急救培训并持有有效急救证书的保育人员，全体从业人员应当掌握基本急救常识和防范、避险、逃生、自救的基本方法，并定期进行事故预防演练。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（四）日常安全</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构从业人员应当具有安全意识和责任感，掌握预防幼儿伤害的相关知识和急救技能，以预防为主，全面关注幼儿的生活和活动安全。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构应当消除各类安全隐患，预防幼儿跌落、摔伤、碰伤、烫伤、烧伤、窒息、中毒、气管异物、异物入体、同伴咬伤、动物致伤、触电、溺水、交通事故等伤害的发生，防止幼儿走失和被拐骗。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　严禁在托育机构内，设置威胁幼儿安全的危险建筑物和设施。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　严禁使用有毒、有害物质制作的教具、玩具。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　入托幼儿应当由幼儿监护人或者其委托的成年人接送。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构一旦发现幼儿遭受或者疑似遭受家庭暴力的，应当依法及时向公安机关报案。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（五）信息及隐私保护</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构及其从业人员对幼儿的个人信息以及隐私，应当予以保护。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第二十一条（卫生保健管理）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构应当严格执行国家有关食品药品安全的法律法规，保障饮食饮水卫生安全，并依据《托儿所幼儿园卫生保健管理办法》及其他有关卫生保健的法规、规章和制度，切实做好幼儿生理和心理卫生保健工作。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（一）健康检查</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　3岁以下幼儿在入托前，应当经区级及以上卫生计生行政部门指定的医疗卫生机构进行健康检查，凭健康检查合格证明方可入托。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构应当根据幼儿的个体发展情况，经征询儿保医生的建议并与家长共同协商，选择采取最有利于幼儿健康成长的托育方式。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（二）疾控预防</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　加强托育机构的卫生保健工作。认真贯彻预防为主、教养融合的工作方针，为幼儿创造良好的生活环境，预防控制传染病，降低常见病的发病率，保障幼儿的身心健康。各级妇幼保健机构、疾病预防控制机构和社区卫生服务机构等要依职责加强对托育机构卫生保健工作的业务指导和咨询服务。卫生计生监督执法机构依法对饮用水卫生、传染病预防和控制等工作进行监督检查。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　（三）制度建设</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　1.建立健全卫生保健制度。为幼儿创造良好的生活环境，预防控制传染病，降低常见病的发病率，逐步培养幼儿良好的生活与卫生习惯，保障幼儿的身心健康。根据幼儿不同月龄段的生理和心理发展特点、幼儿的个体差异和需要，制订合理的幼儿生活制度，科学安排幼儿作息时间和进餐、睡眠、大小便、盥洗、游戏、活动等各个环节的时间、顺序和次数，注意动静结合、室内活动与室外活动结合。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　2.建立健全各项食品安全管理制度。依据相关规定，提供餐食服务。为幼儿提供的食品和饮用水应当符合国家有关卫生标准和规范的要求，加强饮食卫生管理，食品应当留样48小时，确保食品安全。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　根据幼儿的生理需求，为2-3岁幼儿制订膳食计划，编制营养合理、平衡的食谱，提供安全卫生、易于消化吸收、清淡可口、健康的膳食。此外，应当做好食物过敏幼儿的登记工作，提供餐点时应当避免幼儿食物过敏。饮用水和各类食物每日参考摄入量应当符合国家有关卫生保健标准和规范。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　提供全日制托育服务的托育机构应当每周向家长公示幼儿食谱，定期计算和分析幼儿的进食量和营养素摄取量，保证幼儿合理膳食，促进幼儿生长发育。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　3.建立幼儿健康检查制度。托育机构应当为幼儿建立健康检查制度，建立健康档案，开展幼儿定期健康检查工作；做好每日入托时的晨检或午检以及全日健康观察，发现幼儿身体、精神状况、行为等异常的，应当及时处理并通知监护人。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　4.建立卫生与消毒制度。做好环境卫生、个人卫生和预防性消毒工作。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　5.建立传染病防控与管理、常见病预防与管理等制度。做好幼儿传染病的预防与控制以及幼儿常见病的防治等工作，加强健康教育宣传，配合做好预防接种工作。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第二十二条（收费管理）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构（不含托育点）的托育服务收费实行市场调节价管理，由托育机构（不含托育点）根据服务成本、社会承受能力等因素合理制定。托育机构（不含托育点）应当按月收取托育服务费。托育机构（不含托育点）应当向社会公示收费项目和标准以及退费办法等。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　全日制托育机构幼儿伙食费使用情况、半日制和计时制托育机构幼儿点心费使用情况应当按月公示，做到专款专用。托育机构从业人员的餐费应当与幼儿的伙食费严格分清。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　对中途退出托育机构的幼儿，应当提供代办费使用明细账目。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构的收费项目、标准及退费办法等，应当接受所在区市场监督管理部门和业务主管部门的监督检查。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第二十三条（财务管理）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构应当依法建立财务、会计和资产管理制度，建立健全财务内部控制制度。同时，要加强财务和资产管理，所有资产由法人依法管理和使用，任何组织和个人不得截留、挤占、挪用。托育机构资产使用和财务管理，应当接受财政、审计、行业主管部门以及社会各方面的监督检查。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第二十四条（纠纷处理）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构应当与家长或幼儿监护人签订《家长告知书》，明确双方权利、义务、收费项目、金额、退费办法以及纠纷处理方式等相关内容。托育机构不得擅自暂停或者终止服务。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构与幼儿家长发生争议的，可自行协商解决，可申请人民调解组织或者其他有关组织进行调解，也可依法通过仲裁、诉讼等途径解决。</span></p><p style=\"text-align: center; white-space: normal;\"><strong><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">第四章　监督与管理</span></strong></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第二十五条（综合监管）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">&nbsp;</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　建立市、区、街镇三级联动的综合监管机制，畅通投诉举报渠道，加大对托育服务市场违法违规行为的查处力度。在区政府相关职能部门和街镇的统一组织下，形成日常检查发现、归口受理和分派、违法查处等各环节分工牵头负责、共同履职的机制。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　建立日常检查机制。建立举办者自查、区托育服务管理机构网上巡查、区相关职能部门抽查和街镇牵头联合检查相结合的综合监管体系，开展托育服务市场违法违规行为的日常检查发现工作。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　建立归口受理和分派机制。建立由区托育服务管理机构牵头的归口受理机制，对巡查发现或投诉举报的线索进行初步核实，并分派到街镇或者相关职能部门处理。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　建立违法查处机制。托育机构涉及违反多个法律法规规定的，由各街镇牵头，整合区域内执法力量，会同区市场监督管理、消防、公安、卫生计生、教育、人力资源社会保障、民政等职能部门依据法定职责联合执法。涉嫌犯罪的，移交司法部门处理。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　建立诚信评价机制。建立托育机构诚信档案，形成违法失信惩戒制度。将托育机构的违法信息纳入本市公共信用信息服务平台。建立托育机构从业人员的黑名单制度。对托育机构是否符合规定的情况予以信息公告。依法公开托育机构备案登记等基本信息、监督检查结果和行政处罚信息等。加强事中事后监管，提高监测预警能力。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　建立行业自律机制。拓展相关社会团体功能，发挥社会组织业内自律、协调、规范等作用。组织全市托育机构从业人员学习政策法规，增强从业人员依法从业意识，促进行业自律和自觉。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　建立托育服务信息管理平台。充分利用互联网、大数据和智能终端设备，对托育机构的申办过程、综合监管、信息公开、诚信记录、人员信息以及业务数据等进行信息化管理。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第二十六条（日常检查）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　各区托育服务管理机构应当定期组织对托育机构的人员配备、设施设备条件、管理水平、服务质量等开展检查，并将检查结果向社会公布。</span></p><p style=\"text-align: center; white-space: normal;\"><strong><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">第五章　变更与终止</span></strong></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第二十七条（变更流程）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　非营利性托育机构（不含托育点）的登记事项需要变更的，应当自所在区业务主管单位审查同意之日起30日内，向所在区登记管理机关申请变更登记；不同意变更的，应当书面说明理由。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　营利性托育机构（不含托育点）举办者、名称、地址以及其他重大事项变更的，应当在30日内根据有关法律法规规定，向工商（市场监督管理）部门申请变更；不同意变更的，应当书面说明理由。工商（市场监督管理）部门作出变更登记决定后应当及时将信息推送至所在区托育服务管理机构。区托育服务管理机构获得信息后，协调组织相关职能部门及时开展检查。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育点发生地址等重大事项变更的，应当在30日内向所在区托育服务管理机构备案。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第二十八条（终止运营）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育机构（不含托育点）终止服务的，应当按照国家有关规定，办理注销等手续，并做好妥善安置工作。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　非营利性托育机构（不含托育点）终止运营的，应当报所在区业务主管单位审查同意后，依法向所在区民政部门申请注销登记。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　营利性托育机构（不含托育点）终止经营的，应当依法向工商（市场监督管理）部门申请注销登记。</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　托育点终止运营的，应当向所在区托育服务管理机构申请注销备案。</span></p><p style=\"text-align: center; white-space: normal;\"><strong><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">第六章　附则</span></strong></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　第二十九条（施行日期）</span></p><p style=\"white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">　　本办法自2018年6月1日起正式施行，有效期至2020年5月31日。</span></p><p style=\"text-align: center; white-space: normal;\"><span style=\"font-family: 宋体;\"></span><br/></p><p><br/></p>',52,'N','2018-08-22 09:27:35',0,3,-1,-1,-1,'2018-08-22 09:27:35',-1,'2018-09-30 11:29:40',-1,'ID',NULL,NULL,NULL),
 (1066,17,1,'','上海市教委等16部门关于印发《上海市3岁以下幼儿托育机构设置标准（试行沪)》的通知','上海市3岁以下幼儿托育机构设置标准（试行沪)','',NULL,'<p style=\"text-align: center; line-height: normal; white-space: normal;\"><strong><span style=\"color: red; letter-spacing: 24px; font-family: 宋体; font-size: 21px;\">上海市教育委员会</span></strong></p><p style=\"text-align: center; line-height: normal; white-space: normal;\"><strong><span style=\"color: red; letter-spacing: 13px; font-family: 宋体; font-size: 21px;\">上海市发展和改革委员会</span></strong></p><p style=\"text-align: center; line-height: normal; white-space: normal;\"><strong><span style=\"color: red; letter-spacing: 37px; font-family: 宋体; font-size: 21px;\">上海市公安局</span></strong></p><p style=\"text-align: center; line-height: normal; white-space: normal;\"><strong><span style=\"color: red; letter-spacing: 37px; font-family: 宋体; font-size: 21px;\">上海市消防局</span></strong></p><p style=\"text-align: center; line-height: normal; white-space: normal;\"><strong><span style=\"color: red; letter-spacing: 37px; font-family: 宋体; font-size: 21px;\">上海市民政局</span></strong></p><p style=\"text-align: center; line-height: normal; white-space: normal;\"><strong><span style=\"color: red; letter-spacing: 37px; font-family: 宋体; font-size: 21px;\">上海市财政局</span></strong></p><p style=\"text-align: center; line-height: normal; white-space: normal;\"><strong><span style=\"color: red; letter-spacing: 9px; font-family: 宋体; font-size: 21px;\">上海市人力资源和社会保障局</span></strong></p><p style=\"text-align: center; line-height: normal; white-space: normal;\"><strong><span style=\"color: red; letter-spacing: 6px; font-family: 宋体; font-size: 21px;\">上海市住房和城乡建设管理委员会</span></strong></p><p style=\"text-align: center; line-height: normal; white-space: normal;\"><strong><span style=\"color: red; letter-spacing: 9px; font-family: 宋体; font-size: 21px;\">上海市规划和国土资源管理局</span></strong></p><p style=\"text-align: center; line-height: normal; white-space: normal;\"><strong><span style=\"color: red; letter-spacing: 9px; font-family: 宋体; font-size: 21px;\">上海市卫生和计划生育委员会</span></strong></p><p style=\"text-align: center; line-height: normal; white-space: normal;\"><strong><span style=\"color: red; letter-spacing: 24px; font-family: 宋体; font-size: 21px;\">上海市地方税务局</span></strong></p><p style=\"text-align: center; line-height: normal; white-space: normal;\"><strong><span style=\"color: red; letter-spacing: 16px; font-family: 宋体; font-size: 21px;\">上海市工商行政管理局</span></strong><strong><span style=\"color: red; letter-spacing: 9px; font-family: 宋体; font-size: 21px;\"></span></strong></p><p style=\"text-align: center; line-height: normal; white-space: normal;\"><strong><span style=\"color: red; letter-spacing: 11px; font-family: 宋体; font-size: 21px;\">上海市食品药品监督管理局</span></strong><strong><span style=\"color: red; letter-spacing: 9px; font-family: 宋体; font-size: 21px;\"></span></strong></p><p style=\"text-align: center; line-height: normal; white-space: normal;\"><strong><span style=\"color: red; letter-spacing: 37px; font-family: 宋体; font-size: 21px;\">上海市总工会</span></strong><strong><span style=\"color: red; letter-spacing: 9px; font-family: 宋体; font-size: 21px;\"></span></strong></p><p style=\"text-align: center; line-height: normal; white-space: normal;\"><strong><span style=\"color: red; letter-spacing: 24px; font-family: 宋体; font-size: 21px;\">上海市妇女联合会</span></strong></p><p style=\"text-align: center; line-height: normal; white-space: normal;\"><strong><span style=\"color: red; letter-spacing: 24px; font-family: 宋体; font-size: 21px;\">上海市房屋管理局</span></strong><strong><span style=\"color: red; letter-spacing: 9px; font-family: 宋体; font-size: 21px;\"></span></strong></p><p style=\"text-align: center; line-height: normal; white-space: normal;\">&nbsp;<span style=\"font-size: 36px;\">&nbsp;<strong style=\"font-size: 24px;\"><span style=\"color: red; letter-spacing: 24px; font-family: 宋体;\">文件</span></strong></span></p><p style=\"text-align: center; white-space: normal;\"><span style=\"font-family: 黑体; font-size: 19px;\"><span style=\"font-family: 仿宋_GB2312;\">沪教委基〔2018〕27号</span></span></p><hr color=\"#ff0000\" style=\"white-space: normal;\"/><p style=\"text-align: center; white-space: normal;\"><span style=\"font-family: 黑体; font-size: 19px;\"><span style=\"font-family: 仿宋_GB2312;\"></span></span><br/></p><p style=\"text-align: center; white-space: normal;\"><br/></p><p style=\"text-align: center; white-space: normal;\"><span style=\"font-family: 黑体; font-size: 19px;\">上海市教育委员会等16部门关于印发《上海市3岁</span></p><p style=\"text-align: center; white-space: normal;\"><span style=\"font-family: 黑体; font-size: 19px;\">以下幼儿托育机构设置标准（试行）》的通知</span></p><p style=\"text-indent: 43px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">&nbsp;</span></p><p style=\"line-height: 29px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312;\">各区教育局、发展改革委、公安局、消防局、民政局、财政局、人力资源社会保障局、建委、规土局、卫生计生委、地税局、市场监督管理局、工会、妇联、房管局：</span></p><p style=\"line-height: 29px; text-indent: 32px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312;\">为贯彻落实市委、市政府对本市3岁以下幼儿托育服务工作管理的相关要求，规范本市3岁以下幼儿托育机构的设置和管理，市教委、市物价局、市公安局、市消防局、市民政局、市财政局、市人力资源社会保障局、市住房城乡建设管理委、市规划国土资局、市卫生计生委、市地税局、市工商局、市食品药品监管局、市总工会、市妇联、市房管局等16部门研究制订了《上海市3岁以下幼儿托育机构设置标准（试行）》（见附件），现印发给你们，请严格参照执行。</span></p><p style=\"text-align: right; line-height: 29px; text-indent: 32px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312;\"></span></p><p style=\"text-align: right; line-height: 29px; text-indent: 32px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312;\"></span><br/></p><p style=\"line-height: normal; margin-bottom: 0px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312;\">&nbsp;&nbsp; &nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;上海市教育委员会&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"font-family: 仿宋_GB2312;\">上海市发展和改革委员会</span></p><p style=\"line-height: normal; margin-bottom: 0px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312;\">&nbsp; &nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;上海市公安局&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"font-family: 仿宋_GB2312;\">上海市消防局</span></p><p style=\"line-height: normal; margin-bottom: 0px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;上海市民政局&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"font-family: 仿宋_GB2312;\">上海市财政局</span></p><p style=\"line-height: normal; margin-bottom: 0px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp;&nbsp;&nbsp;上海市人力资源和社会保障局&nbsp; &nbsp; &nbsp;</span><span style=\"font-family: 仿宋_GB2312;\">上海市住房和城乡建设管理委员会</span></p><p style=\"line-height: normal; margin-bottom: 0px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp;上海市规划和国土资源管理局&nbsp; &nbsp; &nbsp;</span><span style=\"font-family: 仿宋_GB2312;\">上海市卫生和计划生育委员会</span></p><p style=\"line-height: normal; margin-bottom: 0px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp;&nbsp;上海市地方税务局&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"font-family: 仿宋_GB2312;\">上海市工商行政管理局&nbsp;</span></p><p style=\"line-height: normal; margin-bottom: 0px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp;上海市食品药品监督管理局&nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"font-family: 仿宋_GB2312;\">上海市总工会&nbsp;</span></p><p style=\"line-height: normal; margin-bottom: 0px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&nbsp; &nbsp; &nbsp;上海市妇女联合会&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"font-family: 仿宋_GB2312;\">上海市房屋管理局</span></p><p style=\"text-align: right; line-height: 29px; text-indent: 32px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 20px;\">&nbsp;</span><span style=\"font-family: 仿宋_GB2312;\">2018</span><span style=\"font-family: 仿宋_GB2312;\">年4月28日</span></p><p style=\"line-height: 29px; text-indent: 32px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312;\"></span></p><p style=\"line-height: 16px; white-space: normal;\"><img style=\"margin-right: 2px; vertical-align: middle;\" src=\"http://118.31.3.95:8080/shtyxh/lib/extjs/ueditor/utf8-jsp/dialogs/attachment/fileTypeImages/icon_doc.gif\"/><a title=\"附件：上海市3岁以下幼儿托育机构设置标准.doc\" style=\"color: rgb(0, 102, 204); font-size: 12px;\" href=\"http://118.31.3.95:8080/shtyxh/ueditor/jsp/upload/file/20180727/1532625837241001347.doc\">附件：上海市3岁以下幼儿托育机构设置标准.doc</a></p><p style=\"line-height: 29px; text-indent: 32px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312;\"></span><br/></p><p><br/></p>',53,'N','2018-08-22 09:28:29',0,3,-1,-1,-1,'2018-08-22 09:28:29',-1,'2018-09-30 11:29:21',-1,'ID',NULL,NULL,NULL),
 (1067,17,1,'2','关于加强本市3岁以下幼儿托育机构卫生保健工作的通知','关于加强本市3岁以下幼儿托育机构卫生保健工作的通知','',NULL,'<p style=\"text-align: center; line-height: 27px; white-space: normal;\">&nbsp;</p><p style=\"text-align: center; line-height: 27px; white-space: normal;\"><span style=\"font-family: 黑体;\">&nbsp;</span></p><p style=\"text-align: center; line-height: 27px; white-space: normal;\"><span style=\"font-family: 黑体;\">&nbsp;</span></p><p style=\"text-align: center; line-height: 27px; white-space: normal;\"><span style=\"font-family: 黑体;\">&nbsp;</span></p><p style=\"text-align: center; white-space: normal;\"><strong><span style=\"color: rgb(255, 0, 0); letter-spacing: 1px; font-family: 宋体; font-size: 32px;\">上海市卫生和计划生育委员会文件</span></strong></p><p style=\"white-space: normal;\"><strong><span style=\"color: rgb(255, 0, 0); letter-spacing: 1px; font-family: 宋体; font-size: 32px;\"><span style=\"font-size: 21px;\">&nbsp;</span></span></strong></p><p style=\"margin: 0pt; text-align: center; white-space: normal;\"><span style=\"font-family: 宋体;\"><span style=\"font-family: 仿宋_GB2312; font-size: 16pt;\">沪卫计妇幼〔</span><span style=\"font-family: 仿宋_GB2312; font-size: 16pt;\">2018〕23号</span></span></p><p style=\"text-align: center; white-space: normal;\"><span style=\"font-size: 21px;\"><span style=\"font-family: 宋体;\"></span></span></p><p style=\"text-align: center; white-space: normal;\"><span style=\"font-size: 21px;\"><span style=\"font-family: 宋体;\">&nbsp;<strong><span style=\"color: rgb(255, 0, 0); line-height: 72px; font-size: 48px;\">————————————</span></strong></span></span></p><p style=\"text-align: center; line-height: 24px; white-space: normal;\"><strong><span style=\"line-height: 43.5px; font-family: 华文中宋; font-size: 29px;\">关于加强本市</span></strong><strong><span style=\"line-height: 43.5px; font-family: 华文中宋; font-size: 29px;\">3岁以下幼儿</span></strong></p><p style=\"text-align: center; line-height: 24px; white-space: normal;\"><strong><span style=\"line-height: 43.5px; font-family: 华文中宋; font-size: 29px;\">托育机构卫生保健工作的通知</span></strong></p><p style=\"text-align: center; line-height: 32px; white-space: normal;\"><span style=\"letter-spacing: 0px; font-family: 仿宋_GB2312; font-size: 21px;\"><span style=\"font-family: 宋体;\">&nbsp;</span></span></p><p style=\"line-height: 24px; white-space: normal;\"><span style=\"line-height: 31.5px; font-size: 21px;\"><span style=\"font-family: 宋体;\">各区卫生计生委，申康医院发展中心、有关大学、中福会，市妇幼保健中心、市疾病预防控制中心、市卫生计生委监督所，有关单位:</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 21px;\">为切实贯彻落实市政府《关于促进和加强本市</span><span style=\"font-size: 21px;\">3</span><span style=\"font-size: 21px;\">岁以下幼儿托育服务工作的指导意见》（沪府发〔</span><span style=\"font-size: 21px;\">2018</span><span style=\"font-size: 21px;\">〕</span><span style=\"font-size: 21px;\">19</span><span style=\"font-size: 21px;\">号）、市政府办公厅印发的《上海市</span><span style=\"font-size: 21px;\">3</span><span style=\"font-size: 21px;\">岁以下幼儿托育机构管理暂行办法》</span><span style=\"font-size: 21px;\">(</span><span style=\"font-size: 21px;\">沪府办规〔</span><span style=\"font-size: 21px;\">2018</span><span style=\"font-size: 21px;\">〕</span><span style=\"font-size: 21px;\">12</span><span style=\"font-size: 21px;\">号</span><span style=\"font-size: 21px;\">)</span><span style=\"font-size: 21px;\">、市教委和市卫生计生委等</span><span style=\"font-size: 21px;\">16</span><span style=\"font-size: 21px;\">部门联合下发的《上海市</span><span style=\"font-size: 21px;\">3</span><span style=\"font-size: 21px;\">岁以下幼儿托育机构设置标准（试行）》（沪教委基〔</span><span style=\"font-size: 21px;\">2018</span><span style=\"font-size: 21px;\">〕</span><span style=\"font-size: 21px;\">27</span><span style=\"font-size: 21px;\">号）（以下简称“</span><span style=\"font-size: 21px;\">1+2</span><span style=\"font-size: 21px;\">”文件）的精神，进一步促进本市</span><span style=\"font-size: 21px;\">3</span><span style=\"font-size: 21px;\">岁以下幼儿托育机构卫生保健工作，保障入托幼儿身心健康，现提出以下工作要求：</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 黑体; font-size: 21px;\">一、高度重视，认真实施“</span><span style=\"font-family: 黑体; font-size: 21px;\">1+2</span><span style=\"font-family: 黑体; font-size: 21px;\">”文件</span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-family: 楷体_GB2312; font-size: 21px;\">（一）深刻领会做好托育机构卫生保健的重要性。</span></strong><span style=\"font-size: 21px;\">儿童健康事关家庭幸福和民族未来。婴幼儿阶段是人的全面发展的开端，对其一生健康成长至关重要。神经科学研究成果表明，早期在不良环境成长的儿</span><span style=\"font-size: 21px;\">童，其职业成就差、犯罪率高、慢性疾病发生率高。营养、物理和化学环境因素、病原生物感染、社会心理应激和不良的生活行为等对儿童早期的健康、后续的生长发育和疾病的发生存在关联。对儿童早期发展的投入将支持和促进儿童潜能的最大发挥，获得最佳的回报和产出。</span><span style=\"font-size: 21px;\">促进和加强</span><span style=\"font-size: 21px;\">3</span><span style=\"font-size: 21px;\">岁以下幼儿托育服务工作，对于落实全面两孩政策和十九大报告提出的“幼有所育”目标，以及满足人民群众迫切的托育服务需求，具有深远意义。托育机构是婴幼儿聚集场所，较易引发卫生安全隐患和健康问题，因此加强托育机构卫生保健工作尤为重要。各区卫生计生行政部门和各级妇幼保健、疾病预防控制、卫生计生监督等</span><span style=\"font-size: 21px;\">卫生健康专业机构</span><span style=\"font-size: 21px;\">及各有关医疗卫生机构要切实提高认识，在做好服务的同时严格管理，促进托育服务机构良性发展，真正把这项民生工程做实做好。</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-size: 21px;\">（二）加强组织领导。</span></strong><span style=\"font-size: 21px;\">各级卫生计生行政部门要主动作为，完善夯实包括辖区卫生计生行政部门、医疗卫生机构（包括辖区社区卫生服务中心、二三级医疗机构、疾病预防控制机构、卫生计生监督机构、妇幼保健机构等）的工作网络，明确牵头部门、相关机构和工作人员，</span><span style=\"font-size: 21px;\">强化“责任链”、“实施链”和“绩效链”，</span><span style=\"font-size: 21px;\">落实必要的保障措施。</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-size: 21px;\">（三）切实履行职责。</span></strong><span style=\"font-size: 21px;\">各级妇幼保健、疾病预防控制、卫生计生监督等卫生健康专业机构和相关医疗机构要各司其职、各尽其责、严格把关、齐抓共管，守住“质量安全”底线，确保一方平安。各级妇幼保健、疾病预防控制、卫生计生监督等卫生健康专业机构要加强对本市托育机构卫生保健、疾病防控等工作进行技术指导与支持，依法对托育机构的饮用水卫生、传染病防控等进行监督检查。各社区卫生服务中心要组织医师，包括全科医生、儿保医生、公共卫生医师和卫生专业人员等进入托育机构进行对接和指导。各级医疗机构要做好</span><span style=\"font-size: 21px;\">3</span><span style=\"font-size: 21px;\">岁以下幼儿医疗救治工作，一旦发现多病例就诊，应及时上报有关部门。</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 黑体; font-size: 21px;\">二、各司其职，落实托育机构卫生保健工作</span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-family: 楷体_GB2312; font-size: 21px;\">（一）落实责任主体。</span></strong><strong><span style=\"font-size: 21px;\">各级疾病预防控制机构</span></strong><span style=\"font-size: 21px;\">要协助教育行政部门指导和督促托育机构切实落实传染病预防与控制制度，加强托育机构疫情报告、因病缺课缺勤登记报告、消毒隔离、疫情处置和健康教育等传染病预防控制工作的指导和培训，会同托育机构做好传染病疫情处置。</span><strong><span style=\"font-size: 21px;\">各级卫生计生监督机构</span></strong><span style=\"font-size: 21px;\">依法对托育机构的饮用水卫生、传染病防控等进行监督检查。</span><strong><span style=\"font-size: 21px;\">各级妇幼保健机构</span></strong><span style=\"font-size: 21px;\">要定期对辖区内托育机构卫生保健工作进行业务指导，对托育机构卫生保健人员开展培训，建立完善的评估体系，对辖区内托育机构卫生保健工作进行质控和评估。对新设立的托育机构进行卫生评价，指导托育机构按规定设立保健室或卫生室、配置卫生保健人员、建立并落实卫生保健制度、</span><span style=\"font-size: 21px;\">组织从业人员上岗体检和在岗定期体检、建立</span><span style=\"font-size: 21px;\">健康档案管理制度及</span><span style=\"font-size: 21px;\">开展幼儿新入托和定期健康检查等</span><span style=\"font-size: 21px;\">。</span><span style=\"font-size: 21px;\">做好托育机构新进卫生保健人员上岗及常规培训工作，严格考核，为通过考核的人员颁发合格证</span><span style=\"font-size: 21px;\">。</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-size: 21px;\">（二）加强沟通协作。</span></strong><span style=\"font-size: 21px;\">各级卫生计生行政部门</span><span style=\"font-size: 21px;\">妇幼保健、疾病预防控制、</span><span style=\"font-size: 21px;\">卫生计生监督</span><span style=\"font-size: 21px;\">等相关条线和</span><span style=\"font-size: 21px;\">各级妇幼保健、疾病预防控制、卫生计生监督等卫生健康专业机构及相关医疗机构</span><span style=\"font-size: 21px;\">要加强内部沟通和协作，定期召开工作例会，形成合力，确保各项工作措施有效落实。要加强各级卫生计生行政部门与教育行政部门，卫生健康专业机构、医疗机构与托育服务管理机构、托育机构，卫生专业人员与托育机构卫生保健人员三个层面的工作衔接和沟通协作，</span><span style=\"font-size: 21px;\">及时反馈工作开展情况。</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-size: 21px;\">（三）确保即知、即报、即处。</span></strong><span style=\"font-size: 21px;\">各区卫生计生行政部门要会同区教育行政部门按照“属地化管理”和“分级分类处置”原则，组织做好托育机构食源性疾病事件和传染病疫情处置。由教育行政部门督促托育机构一旦发现疑似食源性疾病事件和传染病疫情，要按照要求立即报告属地社区卫生服务中心或区疾病预防控制中心，同时上报区教育行政部门。区卫生计生和教育行政部门接到报告后应立即组织疾病预防控制机构、相关医疗机构和托育机构及时、规范开展现场流行病学调查，积极、有效落实各项控制措施，如怀疑涉及食品环节的，还要协调食药监部门共同进行调查处置，待查明原因后，再按照职责分工进行处置。各级医疗机构如发现幼儿意外伤害等突发事件或可疑事件，应立即向有关部门报告，并为病重幼儿开辟救治生命的“绿色通道”，提供温馨化服务。</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 黑体; font-size: 21px;\">三、促进发展，鼓励扶持各类机构办好托育机构</span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-family: 楷体_GB2312; font-size: 21px;\">（一）做好开设前卫生评价。</span></strong><span style=\"font-size: 21px;\">区妇幼保健机构要按照指导与评价相结合的原则，对拟新设的托育机构给予指导；接到《托育机构卫生评价申请书》（见附件</span><span style=\"font-size: 21px;\">1</span><span style=\"font-size: 21px;\">）后，在</span><span style=\"font-size: 21px;\">20</span><span style=\"font-size: 21px;\">个工作日内组织相关专业人员，</span><span style=\"font-size: 21px;\">严格</span><span style=\"font-size: 21px;\">依据《托育机构卫生评价表（试行）》（见附件</span><span style=\"font-size: 21px;\">2</span><span style=\"font-size: 21px;\">）进行卫生评价，</span><span style=\"font-size: 21px;\">切实落实室内环境（含甲醛、苯及苯系物等）检测结果符合国家要求、盥洗室内有流动水洗手装置、设立保健室或卫生室、如设卫生室需有《医疗机构执业许可证》、卫生保健人员配备等“必达项目”一票否决，</span><span style=\"font-size: 21px;\">并出具《托育机构卫生评价报告》（见附件</span><span style=\"font-size: 21px;\">1</span><span style=\"font-size: 21px;\">）。对卫生评价“不合格”的机构，要求整改后重新申请评价。</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-size: 21px;\">（二）指导落实卫生保健制度。</span></strong><span style=\"font-size: 21px;\">各相关卫生健康专业机构应指导托育机构严格落实以下</span><span style=\"font-size: 21px;\">11</span><span style=\"font-size: 21px;\">项卫生保健制度：</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-size: 21px;\">1.</span></strong><strong><span style=\"font-size: 21px;\">一日生活制度。</span></strong><span style=\"font-size: 21px;\">根据</span><span style=\"font-size: 21px;\">3</span><span style=\"font-size: 21px;\">岁以下幼儿的生理、心理特点，结合本地区的季节变化和本托育机构的实际情况，制订合理的作息时间和睡眠、进餐、大小便、活动、游戏等制度。</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-size: 21px;\">2.</span></strong><strong><span style=\"font-size: 21px;\">膳食营养制度。</span></strong><span style=\"font-size: 21px;\">参考</span><span style=\"font-size: 21px;\">3</span><span style=\"font-size: 21px;\">岁以下幼儿各类食物每日参考摄入量</span><span style=\"font-size: 21px;\">（见附件</span><span style=\"font-size: 21px;\">3</span><span style=\"font-size: 21px;\">），制订膳食计划。并择时进行膳食调查和营养评估。</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-size: 21px;\">3.</span></strong><strong><span style=\"font-size: 21px;\">体格锻炼制度。</span></strong><span style=\"font-size: 21px;\">根据</span><span style=\"font-size: 21px;\">3</span><span style=\"font-size: 21px;\">岁以下幼儿</span><span style=\"font-size: 21px;\">生理特点，制定每日运动和“日光、空气、水”三浴锻炼制度，保证运动量，提高幼儿身体素质。</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-size: 21px;\">4.</span></strong><strong><span style=\"font-size: 21px;\">卫生与消毒制度。</span></strong><span style=\"font-size: 21px;\">制定环境卫生清扫和检查制度，室内应当有防蚊、蝇、鼠、虫及防暑和防寒设备。对室内空气、阳光、厕所、床及床上用品、玩具、图书的清洁卫生，</span><span style=\"font-size: 21px;\">3</span><span style=\"font-size: 21px;\">岁以下幼儿及工作人员的卫生习惯及各房间、餐桌、易触碰表面的卫生消毒要制定相应制度。</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-size: 21px;\">5.</span></strong><strong><span style=\"font-size: 21px;\">新进及定期健康检查制度。</span></strong><span style=\"font-size: 21px;\">3</span><span style=\"font-size: 21px;\">岁以下幼儿入托育机构前及定期（每年</span><span style=\"font-size: 21px;\">2</span><span style=\"font-size: 21px;\">次）由医疗卫生机构人员进行身体检查；幼儿离园</span><span style=\"font-size: 21px;\">3</span><span style=\"font-size: 21px;\">个月以上需重新健康检查；有晨午检及全日健康观察制度、患病幼儿管理制度、家长委托喂药制度；有工作人员上岗前健康检查、定期健康检查、离岗后重返岗位健康检查制度。</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-size: 21px;\">6.</span></strong><strong><span style=\"font-size: 21px;\">传染病预防与控制制度。</span></strong><span style=\"font-size: 21px;\">须对传染病防控管理、预防接种证查验、传染病疫情及相关突发公共卫生事件报告和处置、日常预防性消毒、因病缺勤缺课登记报告、疑似传染病病例隔离、传染病疫点消毒、日常和传染病流行期间晨午检和全日健康观察、健康教育及痊愈幼儿返园证明等工作制定管理制度和相关预案。</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-size: 21px;\">7.</span></strong><strong><span style=\"font-size: 21px;\">常见疾病预防与管理制度。</span></strong><span style=\"font-size: 21px;\">开展健康教育普及卫生知识，定期开展幼儿眼、耳、口腔保健及筛查诊治，对贫血、营养不良、肥胖等登记管理，加强先心病、哮喘、癫痫等登记观察和护理工作，重视幼儿心理行为保健。</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-size: 21px;\">8.</span></strong><strong><span style=\"font-size: 21px;\">伤害预防制度。</span></strong><span style=\"font-size: 21px;\">建立安全排查制度。房屋、场地、家具、玩教具、生活设施等应当符合国家相关安全标准和规定。应建立重大自然灾害、食物中毒、踩踏、火灾、暴力等突发事件的应急预案及上报流程。加强突发事件应急处理能力和急救技能培训。</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-size: 21px;\">9.</span></strong><strong><span style=\"font-size: 21px;\">健康教育制度。</span></strong><span style=\"font-size: 21px;\">制订全年健康教育工作计划，并组织实施。对健康教育的内容、形式、途径、健康教育记录及健康教育效果均有相应制度。</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-size: 21px;\">10.</span></strong><strong><span style=\"font-size: 21px;\">卫生保健信息收集制度。</span></strong><span style=\"font-size: 21px;\">托育机构应运用信息化方法建立健康档案、对卫生保健工作进行记录，至少保存</span><span style=\"font-size: 21px;\">3</span><span style=\"font-size: 21px;\">年。定期对各项档案数据进行统计分析，掌握幼儿健康及营养状况。</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-size: 21px;\">11.</span></strong><strong><span style=\"font-size: 21px;\">饮用水卫生管理制度。</span></strong><span style=\"font-size: 21px;\">为幼儿提供符合《生活饮用水卫生标准》（</span><span style=\"font-size: 21px;\">GB5749</span><span style=\"font-size: 21px;\">）的生活饮用水（包括冲泡奶粉的水）经煮沸后冷却到</span><span style=\"font-size: 21px;\">20</span><span style=\"font-size: 21px;\">至</span><span style=\"font-size: 21px;\">40</span><span style=\"font-size: 21px;\">摄氏度适宜婴幼儿饮用的开水；</span><span style=\"font-size: 21px;\">&nbsp;1</span><span style=\"font-size: 21px;\">～</span><span style=\"font-size: 21px;\">3</span><span style=\"font-size: 21px;\">岁幼儿饮水量</span><span style=\"font-size: 21px;\">50</span><span style=\"font-size: 21px;\">～</span><span style=\"font-size: 21px;\">100</span><span style=\"font-size: 21px;\">毫升</span><span style=\"font-size: 21px;\">/</span><span style=\"font-size: 21px;\">次</span><span style=\"font-size: 21px;\">×</span><span style=\"font-size: 21px;\">2</span><span style=\"font-size: 21px;\">次</span><span style=\"font-size: 21px;\">/</span><span style=\"font-size: 21px;\">日，酌情调整饮水量。（见附件</span><span style=\"font-size: 21px;\">4</span><span style=\"font-size: 21px;\">）</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><strong><span style=\"font-size: 21px;\">（三）加强经常性卫生保健服务工作。</span></strong><span style=\"font-size: 21px;\">各区卫生计生行政部门要认真贯彻落实《关于在本市中小学和托幼机构开展“医教结合”工作的指导意见》（沪卫疾妇〔</span><span style=\"font-size: 21px;\">2011</span><span style=\"font-size: 21px;\">〕</span><span style=\"font-size: 21px;\">43</span><span style=\"font-size: 21px;\">号）等文件要求，会同教育部门将托育机构卫生保健服务纳入“医教结合”服务范畴，建立“一托一医”工作模式。组织“医生进机构”，与辖区托育机构进行对接，定时、定点、定员进机构开展卫生保健服务。进机构的专业医生对托育机构卫生保健人员进行具体的医疗卫生和保健业务工作指导，必须保证至少每周一次与对接的机构卫生保健人员直接沟通，日常工作中应随时与机构保持联系并及时处理紧急状况。</span></span></p><p style=\"background: rgb(255, 255, 255); line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-size: 21px;\"><span style=\"font-family: 宋体;\">&nbsp;</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 21px;\">附件：</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 21px;\"><img style=\"margin-right: 2px; vertical-align: middle;\" src=\"http://118.31.3.95:8080/shtyxh/lib/extjs/ueditor/utf8-jsp/dialogs/attachment/fileTypeImages/icon_doc.gif\"/><a title=\"附件1：托育机构卫生评价申请书、评价报告.doc\" style=\"color: rgb(0, 102, 204); font-size: 12px;\" href=\"http://118.31.3.95:8080/shtyxh/ueditor/jsp/upload/file/20180727/1532621940656086396.doc\">附件1：托育机构卫生评价申请书、评价报告.doc</a></span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><a title=\"附件2：托育机构卫生评价评价表（试行）.doc\" style=\"color: rgb(0, 102, 204); font-size: 12px;\" href=\"http://118.31.3.95:8080/shtyxh/ueditor/jsp/upload/file/20180727/1532621940660067876.doc\"><img style=\"font-family: 宋体; font-size: 21px; margin-right: 2px; vertical-align: middle;\" src=\"http://118.31.3.95:8080/shtyxh/lib/extjs/ueditor/utf8-jsp/dialogs/attachment/fileTypeImages/icon_doc.gif\"/>附件2：托育机构卫生评价评价表（试行）.doc</a></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><a title=\"附件3：3岁以下幼儿各类食物每日参考摄入量.doc\" style=\"color: rgb(0, 102, 204); font-size: 12px;\" href=\"http://118.31.3.95:8080/shtyxh/ueditor/jsp/upload/file/20180727/1532621940660079718.doc\"><img style=\"font-family: 宋体; font-size: 21px; margin-right: 2px; vertical-align: middle;\" src=\"http://118.31.3.95:8080/shtyxh/lib/extjs/ueditor/utf8-jsp/dialogs/attachment/fileTypeImages/icon_doc.gif\"/>附件3：3岁以下幼儿各类食物每日参考摄入量.doc</a></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><img style=\"margin-right: 2px; vertical-align: middle;\" src=\"http://118.31.3.95:8080/shtyxh/lib/extjs/ueditor/utf8-jsp/dialogs/attachment/fileTypeImages/icon_doc.gif\"/><a title=\"附件4：托育机构饮用水卫生管理制度.doc\" style=\"color: rgb(0, 102, 204); font-size: 12px;\" href=\"http://118.31.3.95:8080/shtyxh/ueditor/jsp/upload/file/20180727/1532621940701054137.doc\">附件4：托育机构饮用水卫生管理制度.doc</a></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 21px;\">&nbsp; &nbsp; &nbsp;</span></span><span style=\"font-family: 宋体; font-size: 21px;\">&nbsp;</span><br/></p><p style=\"text-align: right; line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体; font-size: 21px;\">上海市卫生和计划生育委员会</span><br/></p><p style=\"text-align: right; line-height: 37px; text-indent: 43px; white-space: normal;\"><span style=\"font-family: 宋体;\"><span style=\"font-size: 21px;\">2018</span><span style=\"font-size: 21px;\">年7月9日</span></span></p><p style=\"line-height: 37px; text-indent: 43px; white-space: normal;\"><strong><span style=\"font-family: 仿宋; font-size: 21px;\">&nbsp;</span></strong></p><p style=\"line-height: 37px; white-space: normal;\"><span style=\"letter-spacing: 0px; font-family: 仿宋_GB2312; font-size: 21px;\"><span style=\"font-family: 宋体;\">&nbsp;</span></span></p><p style=\"line-height: 37px; white-space: normal;\"><span style=\"letter-spacing: 0px; font-size: 21px;\"><span style=\"font-family: 宋体;\">&nbsp;</span></span></p><p style=\"white-space: normal;\"><span style=\"font-size: 21px;\"><span style=\"font-family: 宋体;\">&nbsp;</span></span></p><table width=\"583\" cellspacing=\"0\"><tbody><tr class=\"firstRow\" style=\"height: 26px;\"><td width=\"324\" valign=\"top\" style=\"border-color: windowtext rgb(0, 0, 0); padding: 0px; border-right-width: 0px; border-left-width: 0px; border-right-style: none; border-left-style: none;\"><p style=\"line-height: 33px; text-indent: 19px;\"><span style=\"font-size: 19px;\"><span style=\"font-family: 宋体;\">上海市卫生和计划生育委员会办公室</span></span></p></td><td width=\"259\" valign=\"top\" style=\"border-color: windowtext rgb(0, 0, 0); padding: 0px; border-right-width: 0px; border-left-width: 0px; border-right-style: none; border-left-style: none;\"><p style=\"text-align: right; line-height: 33px;\"><span style=\"font-family: 仿宋_GB2312; font-size: 19px;\"><a style=\"background-image: url(&quot;/shtyxh/lib/extjs/ueditor/utf8-jsp/themes/default/images/anchor.gif&quot;);\" name=\"文印日期\"></a><span style=\"font-family: 宋体;\">2018年7月10日印发 &nbsp;</span></span></p><p><span style=\"font-family: 仿宋_GB2312; font-size: 19px;\"><span style=\"font-family: 宋体;\"><br/></span></span></p></td></tr></tbody></table><p><br/></p>',54,'N','2018-08-22 09:29:04',0,2,-1,-1,-1,'2018-08-22 09:29:04',-1,'2018-09-26 10:18:05',-1,'ID',NULL,NULL,NULL);
INSERT INTO `kg_news` (`id`,`typeId`,`sourceId`,`attributeId`,`newsTitle`,`summary`,`thumbnail`,`author`,`content`,`sequence`,`indexshow`,`createDate`,`viewsCount`,`OBJECT_VERSION_NUMBER`,`REQUEST_ID`,`PROGRAM_ID`,`CREATED_BY`,`CREATION_DATE`,`LAST_UPDATED_BY`,`LAST_UPDATE_DATE`,`LAST_UPDATE_LOGIN`,`CERTIFICATE_TYPE`,`EFFECTIVE_START_DATE`,`EFFECTIVE_END_DATE`,`ATTRIBUTE_CATEGORY`) VALUES 
 (1068,17,1,'','上海市教育委员会关于公布2016年至2017年 通过验收的上海市一级幼儿园名单的通知','2016年至2017年通过验收的上海市一级园名单的通知（沪教委基 2018 51号）','',NULL,'<p style=\"text-align: center; white-space: normal;\"><span style=\"color: red; letter-spacing: 12px; font-family: 方正小标宋简体; font-size: 48px;\">上海市教育委员会文件</span></p><p style=\"line-height: 28px; white-space: normal;\"><span style=\"font-family: 黑体; font-size: 20px;\">&nbsp;</span></p><p style=\"line-height: 28px; white-space: normal;\"><span style=\"font-family: 文鼎大标宋简; font-size: 24px;\">&nbsp;</span></p><p style=\"padding: 0px 0px 1px; border: currentColor; border-image: none; text-align: center; line-height: 37px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 20px;\">沪教委基〔2018〕51号</span></p><hr color=\"#ff000\" style=\"white-space: normal;\"/><p style=\"padding: 0px 0px 1px; border: currentColor; border-image: none; text-align: center; line-height: 37px; white-space: normal;\"><span style=\"font-family: 方正小标宋简体; font-size: 25px;\"><br/></span></p><p style=\"padding: 0px 0px 1px; border: currentColor; border-image: none; text-align: center; line-height: 37px; white-space: normal;\"><span style=\"font-family: 方正小标宋简体; font-size: 25px;\">上海市教育委员会关于公布2016年至2017年</span><br/></p><p style=\"text-align: center; line-height: 40px; white-space: normal;\"><span style=\"font-family: 方正小标宋简体; font-size: 25px;\">通过验收的上海市一级幼儿园名单的通知</span></p><p style=\"line-height: 40px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 19px;\">&nbsp;</span></p><p style=\"line-height: 40px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 20px;\">各区教育局、市教育评估院：</span></p><p style=\"line-height: 40px; text-indent: 40px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 20px;\">根据《上海市教育委员会关于上海市托幼协会对本市一级幼儿园、托儿所资质进行审核的通知》（沪教委基〔2002〕69号）和《上海市教育委员会关于进一步规范本市托幼园所办学等级评估办法的意见》（沪教委基〔2003〕87号）要求，经对2016年上半年、下半年和2017年上半年、下半年申报上海市一级幼儿园的单位进行验收，现认定徐汇区印象幼儿园等51所幼儿园为上海市一级幼儿园（具体名单见附件）。</span></p><p style=\"line-height: 40px; text-indent: 40px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 20px;\">希望上述幼儿园在所在区教育局领导下，再接再厉，进一步加强内涵建设，不断提高办园质量，发挥示范辐射作用。希望各区教育局继续加强对区域内托幼园所的指导和管理，进一步提高托幼园所整体办园水平。</span></p><p style=\"line-height: 40px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 20px;\">&nbsp;</span></p><p style=\"line-height: 40px; margin-left: 94px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 20px;\"></span></p><p style=\"line-height: 16px; white-space: normal;\"><img style=\"margin-right: 2px; vertical-align: middle;\" src=\"http://118.31.3.95:8080/shtyxh/lib/extjs/ueditor/utf8-jsp/dialogs/attachment/fileTypeImages/icon_doc.gif\"/><a title=\"附件1 2016年上半年新申报上海市一级幼儿园通过验收的幼儿园名单.doc\" style=\"color: rgb(0, 102, 204); font-size: 12px;\" href=\"http://118.31.3.95:8080/shtyxh/ueditor/jsp/upload/file/20180727/1532629934758051342.doc\">附件1 2016年上半年新申报上海市一级幼儿园通过验收的幼儿园名单.doc</a></p><p style=\"line-height: 16px; white-space: normal;\"><img style=\"margin-right: 2px; vertical-align: middle;\" src=\"http://118.31.3.95:8080/shtyxh/lib/extjs/ueditor/utf8-jsp/dialogs/attachment/fileTypeImages/icon_doc.gif\"/><a title=\"附件2 2016年下半年新申报上海市一级幼儿园通过验收的幼儿园名单.doc\" style=\"color: rgb(0, 102, 204); font-size: 12px;\" href=\"http://118.31.3.95:8080/shtyxh/ueditor/jsp/upload/file/20180727/1532629934766002309.doc\">附件2 2016年下半年新申报上海市一级幼儿园通过验收的幼儿园名单.doc</a></p><p style=\"line-height: 16px; white-space: normal;\"><img style=\"margin-right: 2px; vertical-align: middle;\" src=\"http://118.31.3.95:8080/shtyxh/lib/extjs/ueditor/utf8-jsp/dialogs/attachment/fileTypeImages/icon_doc.gif\"/><a title=\"附件3 2017年上半年新申报上海市一级幼儿园通过验收的幼儿园名单.doc\" style=\"color: rgb(0, 102, 204); font-size: 12px;\" href=\"http://118.31.3.95:8080/shtyxh/ueditor/jsp/upload/file/20180727/1532629934775087257.doc\">附件3 2017年上半年新申报上海市一级幼儿园通过验收的幼儿园名单.doc</a></p><p style=\"line-height: 16px; white-space: normal;\"><img style=\"margin-right: 2px; vertical-align: middle;\" src=\"http://118.31.3.95:8080/shtyxh/lib/extjs/ueditor/utf8-jsp/dialogs/attachment/fileTypeImages/icon_doc.gif\"/><a title=\"附件4 2017年下半年新申报上海市一级幼儿园通过验收的幼儿园名单.doc\" style=\"color: rgb(0, 102, 204); font-size: 12px;\" href=\"http://118.31.3.95:8080/shtyxh/ueditor/jsp/upload/file/20180727/1532629934806037210.doc\">附件4 2017年下半年新申报上海市一级幼儿园通过验收的幼儿园名单.doc</a></p><p style=\"line-height: 40px; margin-left: 94px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 20px;\"></span><br/></p><p style=\"line-height: 37px; white-space: normal;\"><span style=\"font-family: 方正小标宋简体; font-size: 25px;\">&nbsp;</span></p><p style=\"line-height: 37px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 21px;\">&nbsp;</span></p><p style=\"line-height: 37px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 20px;\">&nbsp;</span></p><p style=\"line-height: 37px; text-indent: 310px; margin-right: 24px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 20px;\">上 海 市 教 育 委 员 会</span></p><p style=\"line-height: 37px; text-indent: 350px; margin-right: 24px; white-space: normal;\"><span style=\"font-family: 仿宋_GB2312; font-size: 20px;\">2018</span><span style=\"font-family: 仿宋_GB2312; font-size: 20px;\">年7月10日</span></p><p><span style=\"font-family: 仿宋_GB2312; font-size: 20px;\"><br/></span></p><p><br/></p>',53,'N','2018-08-22 09:29:50',0,3,-1,-1,-1,'2018-08-22 09:29:50',-1,'2018-09-30 11:29:02',-1,'ID',NULL,NULL,NULL),
